INSERT INTO placeholder_texts(code, lang, text) VALUES
    -- WASTELAND
    ('encounter.w.1.desc', 'en', 'Character was attacked by mutants while scavenging in the ruins of a wasteland village.'),
    ('encounter.w.1.desc', 'cs', 'V průběhu prohledávání trosek opuštěné vesnice postavu napadli mutanti.'),
    ('encounter.w.1.succ', 'en', 'Character managed to repel the attackers and collect some of their dropped belongigs.'),
    ('encounter.w.1.succ', 'cs', 'Postava zahnala útočníky na bezhlavý útěk, při němž raději zahodili část svého majetku.'),
    ('encounter.w.1.fail', 'en', 'Character was wounded during the combat and had to flee without having collected anything.'),
    ('encounter.w.1.fail', 'cs', 'V souboji byla postava zraněna a musela utéci, aniž by se jí podařilo cokoliv získat.'),
    ('encounter.w.2.desc', 'en', 'Character was attacked by bandits while scavenging in wasteland.'),
    ('encounter.w.2.desc', 'cs', 'Při cestě pustinou se postava dostala do spárů lapků.'),
    ('encounter.w.2.succ', 'en', 'Character managed to overcome the opponents and stole some caps from one of the fleeing bandits.'),
    ('encounter.w.2.succ', 'cs', 'Postavě se podařilo přemoci překvapené lupiče. Pro spásu svých bídných životů raději nechali postavě několik zátek.'),
    ('encounter.w.2.fail', 'en', 'Character was wounded during the combat and had to flee without having collected anything.'),
    ('encounter.w.2.fail', 'cs', 'V souboji byla postava zraněna a musela utéci, aniž by se jí podařilo cokoliv získat.'),
    ('encounter.w.3.desc', 'en', 'Character was suddenly surprised by a wandering mutated wolf during the exploration of wasteland.'),
    ('encounter.w.3.desc', 'cs', 'Postavu v průběhu prohlídky pustiny náhle překvapil toulavý zmutovaný vlk.'),
    ('encounter.w.3.succ', 'en', 'Character overcame the beast after a short combat and managed to collect some junk on the way back.'),
    ('encounter.w.3.succ', 'cs', 'Postava po krátkém boji zvíře přemohla. Na cestě zpět získala trochu šrotu.'),
    ('encounter.w.3.fail', 'en', 'Character was wounded during the combat and had run back home without having collected anything.'),
    ('encounter.w.3.fail', 'cs', 'V souboji byla postava zraněna a musela utéci, aniž by se jí podařilo cokoliv získat.'),
    ('encounter.w.4.desc', 'en', 'Character was attacked by a mutated cow after passing through an open field.'),
    ('encounter.w.4.desc', 'cs', 'Uprostřed polí napadla postavu zmutovaná kráva. Dvouhlavá, šílená, zřejmě se jmenuje Brahmína.'),
    ('encounter.w.4.succ', 'en', 'Character overcame the beast during the combat and managed to find some junk in the junkyard next to the field.'),
    ('encounter.w.4.succ', 'cs', 'Postava zvíře skolila v souboji. Maso a kůže ze zmutované krávy je nepoužitelné, ale její čtyři výstavní rohy a kopyta se budou ještě hodit.'),
    ('encounter.w.4.fail', 'en', 'Character was wounded during the combat and had run back home without having collected anything.'),
    ('encounter.w.4.fail', 'cs', 'V souboji byla postava zraněna a musela utéci, aniž by se jí podařilo cokoliv získat.'),
    ('encounter.w.5.desc', 'en', 'Character saw a wounded woman in the middle of the road, crying for help.'),
    ('encounter.w.5.desc', 'cs', 'Postava uprostřed cesty spatřila zraněnou ženu prosící o pomoc.'),
    ('encounter.w.5.succ', 'en', 'After stepping a bit closer, the blood on the road did not seem very real and something was moving in the shadows. Our hero avoided this trap and went to collect some junk elsewhere.'),
    ('encounter.w.5.succ', 'cs', 'Postavu zarazilo, že krev na cestě byla příliš zaschlá a neodpovídala zraněním neznámé ženy. V ten moment se stíny v zákoutí u cesty podezřele pohnuly. Postava toto ochotnické představení proto opatrně obešla a šla sbírat šrot někam, kde takovými amatéry nebude rušena.'),
    ('encounter.w.5.fail', 'en', 'After kneeling next to the woman, something stepped out from the nearby shadows and hit our hero in the head. After regaining consciousness, some caps were found missing from the carried bag.'),
    ('encounter.w.5.fail', 'cs', 'Postava důvěřivě poklekla k neznámé ženě, poté však z blízkého zákoutí něco vyběhlo a udeřilo postavu do hlavy. Postava se probrala s pořádnou bolestí hlavy a ochuzena o několik zátek.'),
    ('encounter.w.6.desc', 'en', 'A hidden shelter was found and it seemed abandoned on the first glance. Our character started exploring the hideout but was suddenly attacked by an old man who was apparently living there.'),
    ('encounter.w.6.desc', 'cs', 'Postava nalezla úkryt, který se jevil jako opuštěný, a začala jej prohledávat. Ve skrýši však zřejmě žil jakýsi stařec, který se náhle rozhodl bránit svůj domov.'),
    ('encounter.w.6.succ', 'en', 'Character overcame the inhabitant and took some of his belongings. This is no land for the weak.'),
    ('encounter.w.6.succ', 'cs', 'Postava vyhnala starce z jeho domova a vzala si jeho věci. Asi se sem vrátí, ale bez svého vybavení moc dlouho nepřežije… ale co, tahle země není pro slabé.'),
    ('encounter.w.6.fail', 'en', 'The man turned out to be quite dangerous and our character was wounded during the combat.'),
    ('encounter.w.6.fail', 'cs', 'Stařec se ukázal jako vcelku nebezpečný a zranil postavu v souboji.'),
    ('encounter.w.7.desc', 'en', 'Character encountered a group of merchants and was invited by them for a game of dice poker.'),
    ('encounter.w.7.desc', 'cs', 'Postava potkala skupinu překupníků, kteří jí nabídli partii kostek.'),
    ('encounter.w.7.succ', 'en', 'After playing couple of games, our hero managed to outsmart the other players and left with some additional caps.'),
    ('encounter.w.7.succ', 'cs', 'Po několika hrách se soupeři nechali obrat o několik zátek.'),
    ('encounter.w.7.fail', 'en', 'Character lost couple of games and returned home with nothing.'),
    ('encounter.w.7.fail', 'cs', 'Po několika hrách se postava musela vrátit domů s holým zadkem.'),
    ('encounter.w.8.desc', 'en', 'The wind picked up speed during the exploration of wasteland. Soon enough, blizzard broke out and forced our hero to run for a shelter.'),
    ('encounter.w.8.desc', 'cs', 'Během průzkumu pustiny se začal zvedat vítr. Vánice donutila postavu urychleně hledat přístřeší.'),
    ('encounter.w.8.succ', 'en', 'Character managed to dig a small hole and hid in it until the blizzard passed. Some junk was collected on the way back.'),
    ('encounter.w.8.succ', 'cs', 'Postava vykopala jámu a schovala se v ní, dokud bouře nepřešla. Cestou zpět našla méně šťastné cestovatele. Nebo spíš jejich kusy… ten šrot, který měli u sebe, už jistě nebudou potřebovat...'),
    ('encounter.w.8.fail', 'en', 'Character tried to run to the nearby forest to hide under a tree but was hit by one of the falling branches.'),
    ('encounter.w.8.fail', 'cs', 'Postava se pokusila schovat pod stromy v blízkém lese, ale za tento jalový nápad byla potrestána zásahem jedné z padajících větví.'),
    ('encounter.w.9.desc', 'en', 'Character discovered a closed heavy metal chest in one of the abandonded hideouts. It was locked with something that looked like some kind of strange home-made mechanism.'),
    ('encounter.w.9.desc', 'cs', 'Postava v jedné opuštěné skrýši nalezla těžkou kovovou truhlu. Byla opatřena podivným podomácku vyrobeným zámkem.'),
    ('encounter.w.9.succ', 'en', 'Character spent couple of hours with the chest and managed to open the lock. An item! Nice...'),
    ('encounter.w.9.succ', 'cs', 'Tady hrubá síla nebyla nic platná. Po několika hodinách jemné mechanické práce se podařilo zámek otevřít. A uvnitř - předmět! A moc pěkný...'),
    ('encounter.w.9.fail', 'en', 'Character spent couple of hours trying to open or break the mechanism but was not able to get inside.'),
    ('encounter.w.9.fail', 'cs', 'Po několika minutách pokusu o jemnou mechanickou práci postava změnila taktiku. Ale ani po několika dalších hodinách tvůrčího násilí nebylo možné truhlu otevřít.'),
    ('encounter.w.10.desc', 'en', 'It seems that water is quickly disappearing from this area. As well as life from that wastelander who is lying near the dried up water well. He can’t walk or speak, but he is still alive.'),
    ('encounter.w.10.desc', 'cs', 'Postava se asi příliš vzdálila od obyvatelných oblastí - v průběhu posledních hodin putování Pustinou nenarazila na žádný zdroj vody. Zásoby vody jsou již pryč a postava začíná žíznit. Z těchto míst život asi již nadobro mizí. Stejně jako z toho vandráka, který zalehl u vyschlé strouhy a čeká na svou poslední hodinu, neschopen chodit ani mluvit. Nebo mu je ještě pomoci? '),
    ('encounter.w.10.succ', 'en', 'The character successfully extracted some water from the soil and plants nearby and gave it to the wastelander. He was able to walk once again. It looked out that he had a shelter nearby, so as a thanks he offered the character some of his stock. Quite a lot.'),
    ('encounter.w.10.succ', 'cs', 'Postavě se podařilo dostat vláhu z půdy a odpařit trochu vlhkosti z nasekaných rostlin - ani nebylo potřeba využít močového recyklátu. Nebylo to moc, ale vandráka to dostalo na nohy. Ukázalo se, že docela nedaleko měl pod zemí ukryté zásoby, jen to k nim málem nestihl... Vandrák se z vděčnosti o své poklady s postavou podělil. Dokonce postavu vybavil dostatkem vody na cestu domů. '),
    ('encounter.w.10.fail', 'en', 'The laying wanderer died after a while. The character found out there is no water so there is no point hanging around. The character was terribly thirsty, but - unlike the ex-wanderer - stayed alive.'),
    ('encounter.w.10.fail', 'cs', 'Poblíž žádná voda prostě není. Postava se - samozřejmě po prohledání mrtvoly, která u sebe neměla nic cenného - rychle vydala na cestu zpět. Sice několik hodin žíznila, ale narozdíl od toho vandráka přežila.'),
    ('encounter.w.11.desc', 'en', 'The character met a confused wanderer. He said his shelter was somewhere nearby but he couldn’t find it. Perhaps, the shelter could be found by following the description...'),
    ('encounter.w.11.desc', 'cs', 'Postava se už vracela domů, když na cestě potkala zmateného ztraceného člověka. Prý má někde poblíž svůj kryt a nemůže ho najít. Dle popisu postava možná i tuší, kde by to tak mohlo být...'),
    ('encounter.w.11.succ', 'en', 'The description was correct, the character really found the shelter and received a nice reward from the lost wastelander.'),
    ('encounter.w.11.succ', 'cs', 'Popis seděl, postava skutečně úkryt našla. Za svou pomoc dostala zaslouženou odměnu.'),
    ('encounter.w.11.fail', 'en', 'The character spent a few hours searching for the shelter. Unfortunately, the search wasn‘t successful.'),
    ('encounter.w.11.fail', 'cs', 'Postava společně s cizincem několik hodin pobíhala marně po okolí, dokud neztratila trpělivost a ani přes příslib velké odměny svého počínání nezanechala. Cizinec pokračoval v hledání, zřejmě hledá dodnes. Dobře mu tak, postava kvůli němu do klanu nepřinesla nic užitečného.'),
    ('encounter.w.12.desc', 'en', 'The character found an encampment full of dangerous rogues and scoundrels. It looks like they are planning something huge. It would be helpful to find out what is going on.'),
    ('encounter.w.12.desc', 'cs', 'Postava náhodou objevila tábor plný nebezpečných ničemů. O jejich působení se traduje několik nepěkných historek. Vyrážejí pravidelně přepadávat široké okolí a zřejmě mají nějaké plány do budoucna. Jeden člověk s nimi sotva něco zmůže, ale kdyby někdo potají zjistil jejich plány, bylo by to užitečné.'),
    ('encounter.w.12.succ', 'en', 'The character was able to secretly overhear an important conversation inside the camp and found out some interesting information.'),
    ('encounter.w.12.succ', 'cs', 'Postava, nikým nespatřena, vyslechla několik důležitých rozhovorů. Zjistila tak zajímavé informace.'),
    ('encounter.w.12.fail', 'en', 'Unfortunately, there was a guard nearby. The character caught his attention and has to hope for a quick fight and run away.'),
    ('encounter.w.12.fail', 'cs', 'Postava na sebe upoutala nechtěnou pozornost strážného, který ji ihned napadl. S trochou štěstí se postavě podaří strážného zahnat na útěk a dostat se v klidu domů.'),
    ('encounter.w.13.desc', 'en', 'Several odd-looking wastelanders were harassing a lone traveller. It was obvious he had nothing of value. The attackers were annoyed of his poverty. It seems they will not leave without bloodshed. Suddenly they spotted incoming character.'),
    ('encounter.w.13.desc', 'cs', 'Několik slabě, ale odhodlaně vypadajících vandráků dotíralo na jediného pocestného. Ten vypadal, že jeho jedinou obranou je chudoba - zjevně u sebe neměl nic zajímavého. Útočníci z toho byli dost rozladěni a nevypadali, že by odešli bez krveprolití. Pak si všimli právě příchozí postavy...'),
    ('encounter.w.13.succ', 'en', 'The character confidently started to play the role of a local peacekeeper and started to investigate the incident. The wastelanders were convinced to withdraw. The stranger travelled with the character for a while. The character learned a lot about the life in the Wasteland.'),
    ('encounter.w.13.succ', 'cs', 'Postava se sebevědomě prohlásila za místního ochránce pořádku a jala se incident vyšetřovat. Na vandráky to zapůsobilo. Nebo jen pochopili, že nemají co získat. Každopádně se rychle sebrali a zmizeli. Cizinec pak s postavou chvíli cestoval a podělil o své velké zkušenosti z Pustiny. Díky tomu se postava dozvěděla mnoho zajímavých informací.'),
    ('encounter.w.13.fail', 'en', 'The character was not able to behave properly. The wastelanders panicked. One of them quickly killed the stranger with a sharpened stick and attacked the character. He will surely run after a few hits, but that won’t help the dead stranger, anyway. '),
    ('encounter.w.13.fail', 'cs', 'Postava na situaci nedovedla správně zareagovat. Otrhanci zpanikařili. Jeden z nich chudáka rychle propíchl naostřeným klackem a vrhl se do nového boje. Když mu postava dá několik zasloužených ran, dají se jistě na útěk, ale tomu cizinci to život už nevrátí.'),
    ('encounter.w.14.desc', 'en', 'The character met a man with a huge, smoking, damaged vehicle. It did not seem he was going with it anywhere anytime soon. The stranger probably needs some help with repairing..'),
    ('encounter.w.14.desc', 'cs', 'Na cestě postava potkala cizince s nějakým velkým, čoudícím, porouchaným zařízením. Zřejmě se jedná o nějaké vozidlo, které by mělo umět samo jezdit, ale zrovna přestalo. Cizinec se přehrabuje ve vnitřnostech stroje a nejspíš potřebuje pomoci. '),
    ('encounter.w.14.succ', 'en', 'The character has never seen such a vehicle before. Thanks to the knowledge of mechanics ourcharacter knew what to do. The driver was grateful and offered to take the character for a ride across the Wasteland. The character was able to learn lot more compared to travelling by foot.'),
    ('encounter.w.14.succ', 'cs', 'Ačkoliv postava toto zařízení neznala, díky svým znalostem mechaniky přesně věděla, kde se má co opravit. Funguje to a jede, to je zázrak! Postava se nechala po zbytek dne vozit pustinou, díky čemuž se o Pustině dozvěděla mnohem více, než kdyby šla pěšky. Domů se vrátila s vozem naloženým hodnotnými surovinami.'),
    ('encounter.w.14.fail', 'en', 'The character agreed to repair the vehicle. It just needed to pull the lever here, fix the cabel there, and… whoops, the engine is on fire now... The stranger tried to put out the fire, the fire burnt him terribly. Our character suffered burns as well.'),
    ('encounter.w.14.fail', 'cs', 'Postava se zkušeným okem podívala na neznámé zařízení, zatáhla za páčku tady, vytáhla kabel támhle, a… poté podivná věc náhle vzplanula mocným čoudivým tmavým plamenem. Cizinec se oheň pokusil uhasit, bohužel vzplanul také. Postava si z toho odnesla popáleniny a mírný pocit zodpovědnosti za zmařený lidský život a za to, že pravděpodobně převratný vynález upadl do zapomnění.'),
    ('encounter.w.15.desc', 'en', 'The character met a stranger, probably a member of a different clan. He carried a lot of useful survival tools. The stranger shouted with a strong accent: “You are not a Stalker! How dare you enter the surface? You must be a mutant!” The character was not able to prove his humanity so the stranger immediately attacked. '),
    ('encounter.w.15.desc', 'cs', 'Daleko od domova postava potkala cizince, asi patřícího k jinému klanu. Byl mnohem lépe vybavený než postava, nesl s sebou spoustu praktických věcí do Pustiny. Z dálky na postavu volal se silným cizím přízvukem: “Ty nejsi Stalker! Jak to, že jsi na povrchu? Tak to musíš být mutant!” Postava nezvládla včas prokázat svou nemutantskost a cizinec ihned zaútočil.'),
    ('encounter.w.15.succ', 'en', 'The mutant hunter left a valuable treasures after the battle.'),
    ('encounter.w.15.succ', 'cs', 'Lovec mutantů po sobě zanechal cenné poklady.'),
    ('encounter.w.15.fail', 'en', 'The Stalker-searching stranger was a strong opponent and defeated the character.'),
    ('encounter.w.15.fail', 'cs', 'Cizinec hledající nějakého Stalkera se v souboji uměl dost ohánět a postavu porazil.'),
    ('encounter.w.16.desc', 'en', 'There are plenty of devastated houses in the Wasteland. The character was able to read a sign near a former entrance gate, but it was in some antique language - “Budujeme nový, horší svět.” It seemed like a total gibberish, but still, there could be some useful stuff in there. '),
    ('encounter.w.16.desc', 'cs', 'Hluboko v Pustině bylo několik rozpadlých domů, na nichž se postavě podařilo rozluštit nápis u bývalé vstupní brány - “Budujeme nový, horší svět”. Ať to napsal kdokoliv, zřejmě tím označil zajímavé místo. Že by konečně něco užitečného?'),
    ('encounter.w.16.succ', 'en', 'Unfortunately, almost all the houses were empty. All but one. The sign, which said “Junktown 2019”, forced the character to contemplate about the past and present times. The character learned a lot from the meditation. Who were these Junktown people, and why were there 2019 of them?'),
    ('encounter.w.16.succ', 'cs', 'Domy jsou opravdu opuštěné. Zajímavý byl vlastně jen téměř neviditelný nápis “Junktown 2019”, který postavu přiměl k rozjímání o událostech dávno minulých. Meditací si postava uvědomila mnoho věcí o sobě a současném světě a získala zkušenosti navíc. Přesto má pocit, že svět skýtá ještě mnoho otázek k zodpovězení. Kdo to byli ti junktowni, a proč jich bylo 2019? '),
    ('encounter.w.16.fail', 'en', 'Not really. The only thing our character found were couple of desolated houses with a lot of graffiti.'),
    ('encounter.w.16.fail', 'cs', 'Bohužel ne, postava po několika hodinách marného hledání nedokázala najít vůbec nic cenného, jen rozpadlé baráky počmárané nápisy. '),
    ('encounter.w.17.desc', 'en', 'Character encountered a drowning man, trapped in a strong current of one of the local rivers.'),
    ('encounter.w.17.desc', 'cs', 'Postava se zaradovala, protože našla větší řeku a tím zřejmě i zdroj potravin. Podle všeho se ale řeka před nedávnem vylila z koryta. A poté zalila nějakého nešťastníka, který se zmítá v proudu. Tonoucí za chvíli propluje kolem postavy...'),
    ('encounter.w.17.succ', 'en', 'The unfortunate man was rescued by a quick action of our hero, who handed him a stick and pulled him out of the current. The rescued man told the character that he was trying to pull out a container from the river and fell. The container was later collected by our character who found some food in it.'),
    ('encounter.w.17.succ', 'cs', 'Postava duchapřítomně nešťastníkovi podala pomocnou větev a tím ho zachránila od jisté smrti. Po vylovení přiznal, že se kousek od proudu výš pokusil z řeky vylovit kontejner zaseklý u břehu. To postavu zaujalo a protože měl být kontejner plný zásob, vylovila jej sama. Jídlo navíc se vždy hodí.'),
    ('encounter.w.17.fail', 'en', 'After an unsuccessful rescue attempt, our character fell into the river as well and lost all carried caps.'),
    ('encounter.w.17.fail', 'cs', 'Po chvíli očumování postava uklouzla a začala se topit také. O několik metrů níže vyplavala, ale voda s sebou vzala všechny zátky, které měla u sebe.'),
    ('encounter.w.18.desc', 'en', 'It is not rare to encounter an armed salesman when travelling across Wasteland. They are usually not violent but some of their practices can be considered a bit aggressive. Just like now - our character met such salesman offering "very convenient" goods to a poor Wastelander.'),
    ('encounter.w.18.desc', 'cs', 'Pustinou někdy táhnou i ozbrojení obchodní cestující. Nebývají násilní, ale některé jejich praktiky se dají považovat za agresivní. Třeba jako teď, když postava potkala jiného vandráka, který se vehementně brání výhodným nabídkám typu “Nechceš pěkně savej tampón? A co takhle mastnej šampón?”'),
    ('encounter.w.18.succ', 'en', 'Our hero thwarted the trade by helping the Wastelander with his arguments. The salesman angrily packed this things and set out to seek another victim. Our character received some food as a token of gratitude from the wannabe-customer.'),
    ('encounter.w.18.succ', 'cs', 'Postava zmařila podvodníkův prodej roku tím, že oběti poradila kouzelnou formuli, která ji původně ani nenapadla - “Nechci, nechci.” Naštvaný obchodník se svou károu zbytečností odtáhl dál, vděčná oběť se podělila o trochu jídla, které původně měla v úmyslu zaplatit prodavači.'),
    ('encounter.w.18.fail', 'en', 'Our character spent some caps for a collection of jugs what broke couple of minutes after the salesman left to seek another victim. But it is nothing compared to the other "happy" customer who gave the scammer all his food for a bag of comestics and overpriced soap.'),
    ('encounter.w.18.fail', 'cs', 'Postava utratila několik zátek za sadu zbytných hrnců, které však při prvním použití praskly. Ale pořád je na tom lépe než ten druhý vandrák, který žádné zátky neměl a tak za sadu zázračné kosmetiky utratil všechno své jídlo. Kvůli tomu se navíc zadlužil, a aby nezemřel hlady, snědl veškeré zásoby svého předraženého mýdla. Prý okamžitě zemřel na otravu.'),
    ('encounter.w.19.desc', 'en', 'The character was attacked by a group of hungry cannibals from the Wasteland. They had only one decent fighter. Defeat this one and the others will surely flee.'),
    ('encounter.w.19.desc', 'cs', 'Postavu si jako chutné sousto vyhlédlo několik podvyživených kanibalů z Pustiny. Solidního bojovníka mezi sebou měli jen jednoho. Stačí vyřadit toho jediného z boje a ostatní se jistě dají na útěk.'),
    ('encounter.w.19.succ', 'en', 'The character defeated the strongest enemy and frightened the others away. They even left some useful treasure behind.'),
    ('encounter.w.19.succ', 'cs', 'Postava přesně podle plánu porazila nejnebezpečnějšího soupeře a ostatní se rozprchli po okolí. Musí své zvrácené hladové žaludky nakrmit jiným způsobem. Postava cestou domů narazila na jejich opuštěný tábor. Veškeré jídlo tam raději nechala, ale našla užitečný předmět.'),
    ('encounter.w.19.fail', 'en', 'They were really meagre, nevertheless dangerous. The character lost the fight, but at least wasn’t taken captive.'),
    ('encounter.w.19.fail', 'cs', 'Byli vyhublí, ale přesto nebezpeční. V nerovném boji postava prohrála, naštěstí se kanibalům nepodařilo postavu zajmout.'),
    ('encounter.w.20.desc', 'en', 'There was a cruel fight between two bands of scoundrels in the Wasteland. Members of  one of the groups had outfits decorated with bones. Our character accidently appeared just in time of the brawl. One of the scoundrels spotted the character: “Help us, we will pay.”'),
    ('encounter.w.20.desc', 'cs', 'Postava se v Pustině nepozorovaně připletla k pranici, zrovna mezi sebou na život a na smrt zápasily dvě několikačlenné bandy. Oblečení jedné ze skupin bylo ozdobeno doplňky z kostí. Velitel druhé skupiny si postavy všiml: “Pomož nám, zaplatíme!” Ostatní postavě nevěnují pozornost. Náhle jeden z bojujících soupeřů - muž s obrovským mrožím knírem - zakopl a padl na zem. Toho využil jakýsi fešák s helmou vyrobenou z lebky nějakého děsivého mutanta. Pozvedl svou sekeru, knírač stihl jen dát ruku před obličej… '),
    ('encounter.w.20.succ', 'en', 'The character successfully backstabbed one of the bony strangers and easily decided the battle. The leader of the winning side gladly payed the character some caps.'),
    ('encounter.w.20.succ', 'cs', 'Postava okamžitě vpadla do zad kostlivému sekerníkovi a vyřadila jej jediným úderem z boje. Jeho spolubojovníci byli krátce na to rovněž poraženi. Velitel skupiny postavě poděkoval: “Zachránil jsi tady Satinskýho. Za to máš náš vděk. Tu sekeru si nechám já, odpornou helmu spálíme jako úlitbu Atomovi, a tobě za pomoc samozřejmě zaplatíme.”'),
    ('encounter.w.20.fail', 'en', 'We were not able to help anyone in the brawl. The skeletal group started to tak over so our character ran away.'),
    ('encounter.w.20.fail', 'cs', 'Postava nedokázala nijak zabránit přesně provedené dekapitaci. Krátce na to začali kostliví válečníci drtivě vítězit. Nevypadali zrovna jako sympaťáci dne, proto postavě raději zvolila bezhlavý útěk až domů.'),
    ('encounter.w.21.desc', 'en', 'At the bottom of the mountain pass, the character was startled by the sound of rolling stones. It sounded like a solid rock`n`roll, but it was actually a hundred-ton avalanche approaching the character quickly.'),
    ('encounter.w.21.desc', 'cs', 'Na dně horského průsmyku postavu zaskočil zvuk valících se kamenů. Znělo to jako solidní rock`n`roll, ale ve skutečnosti šlo o stovky tun těžkou lavinu blížící se rychle k postavě.'),
    ('encounter.w.21.succ', 'en', 'The character immediately found a great place which the avalanche would not reach. When the character later climbed out, he saw a tasty-looking animal which died in the fallen stones. The vision of future meal was a sufficient satisfaction for the character for the suffering experienced.'),
    ('encounter.w.21.succ', 'cs', 'Postava se rozhlédla a ihned našla skvělé místo, kam se lavina nemá šanci dostat. Když postava po skončení všeho toho rámusu vylezla ven, mezi popadaným kamením spatřila chutně vypadající zavalené zvíře. Vidina budoucího jídla byla pro postavu dostatečným uspokojením za prožité strasti.'),
    ('encounter.w.21.fail', 'en', 'The character hastily found a place which the avalanche would not reach. Unfortunatelly, it turned out that it was not so great - a few fragments came into shelter and slightly injured the character. But it could have been worse.'),
    ('encounter.w.21.fail', 'cs', 'Postava se rozhlédla a včas našla místo, kam se lavina určitě nedostane. No, nakonec nebylo zas tak vhodné - několik úlomků se do zákrytu dostalo a postavu mírně zranilo. Ale mohlo to dopadnout hůř.'),
    ('encounter.w.22.desc', 'en', 'An annoying guy joined the character on the way. In addition, he smelled disgusting. It was impossible to get rid of him. Neither the guy nor the smell.'),
    ('encounter.w.22.desc', 'cs', 'K postavě se na cestě přidal otravný týpek. Navíc značně zapáchal. Nebylo možné se ho taktně zbavit. Ani týpka, ani zápachu.'),
    ('encounter.w.22.succ', 'en', 'The only way to get rid of him was to be rude. "Tell me, how come you are so annoying and so smelly?" He lectured the character for a short time before he finally took offense and disappeared. That evening, the character had wash thoroughly at the clan base to get rid of the smell. Nevertheless, the character received interesting information from the reek.'),
    ('encounter.w.22.succ', 'cs', 'Bylo nutné vsadit na jistotu - být nezdvořilý a zbavit se ho navždy. “Pověz, jak to, že jsi tak otravný a tak páchnoucí?” Dotyčný postavě ještě krátce zapřednášel, než se definitivně urazil a zmizel. Ten večer se postava musela v klanové bázi drhnout obzvlášť poctivě, aby zápach pustil. Přesto od smraďocha získala zajímavé informace.'),
    ('encounter.w.22.fail', 'en', 'At the first opportunity, he stole a few caps and disappeared to bother someone else. Actually, he is gone, which is quite a good result. '),
    ('encounter.w.22.fail', 'cs', 'Při nejbližší příležitosti obral postavu o několik zátek a zmizel otravovat někoho jiného. Nakonec docela dobrý výsledek, hlavně, že je pryč.'),
    ('encounter.w.23.desc', 'en', 'On the unfrequented road, a brigand looted the carriage of a trader who had just been murdered. He plundered very systematically - he ate food, drank alcohol, and destroyed the rest. He immediately attacked the figure, armed with a deadly combination of a glass bottle and a metal fork.'),
    ('encounter.w.23.desc', 'cs', 'Na opuštěné cestě raboval jakýsi lapka povoz právě zavražděného obchodníka. Plenil velice systematicky - jídlo jedl, alkohol pil, zbytek ničil. Na postavu okamžitě zaútočil, ozbrojen smrtící kombinací lahve s uraženým dnem a kovové vidličky.'),
    ('encounter.w.23.succ', 'en', 'The brigand ran away in horror, leaving his fork in place. This proved to be a valuable source of junk. Unfortunately, there was not much left of the trader`s property.'),
    ('encounter.w.23.succ', 'cs', 'Lapka s hrůzou utekl a na místě zanechal i svou vidličku. Ta se ukázala jako cenný zdroj šrotu, bohužel z povozníkova majetku už toho jinak moc nezbylo.'),
    ('encounter.w.23.fail', 'en', 'The fork turned out to be a dangerous weapon, it had to add at least 3 to brigand’s combat ability. This is not easy to beat…'),
    ('encounter.w.23.fail', 'cs', 'Vidlička se ukázala jako nebezpečná zbraň, to mu muselo přidat minimálně 3 k bojeschopnosti. To nešlo porazit...'),
    ('encounter.w.24.desc', 'en', 'One of the least popular things which can happen is to step right into the bear trap.'),
    ('encounter.w.24.desc', 'cs', 'Jedna z nejméně příjemných věcí, co se vám může stát, je šlápnout neopatrnou nohou přímo do pasti na medvěda.'),
    ('encounter.w.24.succ', 'en', 'This experience was very painful. The character managed to open the trap almost immediately, so the injury was minimal. The trap turned out much worse - the character disassembled it into small parts of a valuable junk.'),
    ('encounter.w.24.succ', 'cs', 'Bylo to dost bolestivé, ale past se podařilo téměř ihned otevřít a tak zranění bylo jen minimální. Past dopadla mnohem hůře, postava ji rozebrala na malé součástky, díky čemuž si z výpravy přinesla množství kvalitního šrotu.'),
    ('encounter.w.24.fail', 'en', 'The character failed to open the trap peacefully. There were only two basic variants left - destroy the leg, or destroy the trap. The combination of both won. Fortunately only the trap remained unusable in the end. The character lost a lot of blood but is still able to walk.'),
    ('encounter.w.24.fail', 'cs', 'Rozevřít past se postavě nepovedlo. Základní varianty tak zůstaly jen dvě - zničit nohu nebo zničit past. Zvítězila kombinace obojího, naštěstí nakonec nepoužitelná zůstala jen ta past. Noha použitelná bude, naštěstí to odnesla jen ošklivými ránami a ztrátou spousty krve.'),
    ('encounter.w.25.desc', 'en', 'As was the character wandering through an unexplored part of the Wasteland in search for resources, it came upon a steep gorge. A piece of clothing was hanging from a tree nearby so the character decided to take the risk and climb down the gorge. An old man with a nasty looking femoral fracture was lying at the bottom. He was obviously too weak to climb up but the wound was surprisingly clean without any sign of inflammation. The character decided to help the old man, carried him out of the gorge and helped him make a splint. The old man was extremely grateful and provided the character with the description of the process to make a anti-inflammatory healing lotion made from a plant growing in the gorge. The character made sure he remembered all the procedures and plucked a handful of those herbs on its way home.'),
    ('encounter.w.25.desc', 'cs', 'Když postava prozkoumávala vzdálenou část Pustiny, kde ještě nikdo z klanu nebyl, zahlédla viset na stromku v rokli cár oblečení. Protože z toho vždycky může něco kápnout, postava nelenila a pomocí lana se spustila do rokle. S překvapením tu nalezla polomrtvého starce s ošklivě zlomenou nohou. Byl příliš slabý, aby se vyškrábal zpět nahoru, ale ránu, která by měla být dávno zanícená, měl krásně čistou. Postava mu pomohla z rokle a dala mu najíst a napít a pomohla vytvořit dlahu, aby měl alespoň šanci přežít. Dojatý stařec postavě podrobně popsal, že v téhle rokli se nachází léčivé byliny, ze kterých dělá skvělou protizánětlivou mast a popsal postavě postup její výroby. Postava si pak na cestu domů nabrala plný batoh léčivých bylin a cestou domů si pro sebe opakovala postup výroby.'),
    ('encounter.w.25.succ', 'en', 'Upon arriving at the base the character gathered all the ingredients and after few hours of grinding and mixing managed to create a foul-smelling concoction. Nobody else offered to work as a lab rat so the character had to apply to lotion on its bruises...and woke to a surprise of all of them having disappeared.'),
    ('encounter.w.25.succ', 'cs', 'Postava ihned po návratu domů sehnala potřebné ingredience a po několika hodinách drcení, míchání a hnětení vytvořila nevábně zavánějící pastu. I přes naléhání nikdo z klanu nechtěl sloužit jako pokusný králík, takže si musela postava své šrámy namazat sama. A světe div se...do druhého dne že zhojily!'),
    ('encounter.w.25.fail', 'en', 'The character was sure to follow the instructions precisely but was not really sure when it applied a black viscous liquid on its cuts and bruises...and for a reason... either the character managed to screw up to procedure or the old man played a very nasty trick on him. Either way, the character spent three nights with high fever and felt like its every bone has been broken for another week...at least we learned a valuable lesson - we can``t trust anyone in this damn world...'),
    ('encounter.w.25.fail', 'cs', 'Postava si byla jistá, že dodržela všechny dědkem popsané postupy, přesto si nebyla jistá, když si večer potírala svoje rány černou viskózní tekutinou. A měla proč... buď musela něco pomotat nebo ji chtěl ten zatracený dědek zabít. Postava se zmítala v horečkách další tři dny a ještě týden poté byla jako rozlámaná. V týhle zemi se prostě nedá věřit nikomu...'),
    ('encounter.w.26.desc', 'en', 'The character was just exploring a wooded hill far from the base when he heard a swish of a knife that had missed its face only by a few centimeters. The character turned but didn`t see anyone. The second blade found its target and the character screamed with pain. Only then did he notice a person covered with mud and a tree bark standing quite close. Without further hesitation the character charged the sneaky attacker.'),
    ('encounter.w.26.desc', 'cs', 'Během prozkoumávání zalesněného kopce daleko od základny překvapil postavu zvuk letícího nože, který minul její obličej jen o několik centimetrů. Postava se otočila, ale nemohla útočníka objevit. Druhý nůž už našel cíl a postava se zařvala bolestí. Teprve teď si všimla chlapa pokrytého blátem a stromovou kůrou. Postava na nic nečekala a vrhla se na toho zákeřného vandráka.'),
    ('encounter.w.26.succ', 'en', 'The character managed to overcome the attacker. He tried to run but was caught. He pleaded for mercy but was given none. Such back-stabbers do not deserve to live. His body was not even cold and the character already thought about the number of unwanted encounters it was going to avoid with this new camo outfit.'),
    ('encounter.w.26.succ', 'cs', 'Postava nakonec dokázala útočníka přemoci. Utíkal, ale postava ho dohnala a srazila k zemi. Prosil o milost, ale kdo ví, koho by ještě přepadl ze zálohy. Takoví si milost nezaslouží. Jeho tělo ještě nevychladlo a postava už zkoumala jeho maskovací ohoz a přemýšlela, kolika nepříjemným setkáním se díky němu vyhne.'),
    ('encounter.w.26.fail', 'en', 'The first knife definitely wasn`t the last to end in the characters body. The character had to perform a risky jump from a nearby rock to escape. We are definitely not going to these parts ever again.'),
    ('encounter.w.26.fail', 'cs', 'Ten nůž rozhodně nebyl poslední, který se do postavy zakousl. Může být ráda, že se jí podařilo riskantním skokem z nedaleké skály útočníkovi utéct. Pro příště se rozhodně na tuhle stranu nevydá.'),
    ('encounter.w.27.desc', 'en', 'The character was crossing a flooded creek when it heard a desperate whine of a completely soaked puppy stranded on a rock in the middle of the current. The character, overcome with compassion, decided to help the poor soul. With great effort, the character threw itself into the current and managed to withstand its power and come out with the dog clutched to its chest. The dog looked extremely grateful and the character decided to forge a new friendship and offer some dried meat to the little ball of fur.'),
    ('encounter.w.27.desc', 'cs', 'Při překonávání rozvodněného potoka postava zaslechla zoufalé kňučení a všimla si sotva odrostlého štěněte zoufale se snažící vylézt na kámen uprostřed proudu. Postavě se chlupáče zželelo a rozhodla se mu pomoci. Za cenu velkého vypětí vzdorovala proudu, popadla psa a vrhla se na břeh. Pes vypadal opravdu vděčně, postavu napadlo, že by ho mohlo použít jako pomocníka při hledání a lovu. Vytáhla sušené maso a nabídla ho psovi.'),
    ('encounter.w.27.succ', 'en', 'Although initially wary, the dog ate the meat with an appetite as if it has starved for a year. The character was able to scratch him behind the ear but when it was time to return to the base, the dog ran away. The character cursed the wasted effort and food but when it was approaching the base, it heard a dog barking. It was the same puppy but this time, it carried a hare in its muzzle. It seems like a friendship was made today.'),
    ('encounter.w.27.succ', 'cs', 'Pes se po sušeném mase jen oblízl. Dokonce se i nechal podrbat za uchem. Když se ale pak postava chtěla vydat směrem k základně, odběhl. Postava si v duchu vynadala, že plýtvala silami a jídlem. Když už se zklamaná a mokrá postava blížila základně, uslyšela zaštěkání. Byl to chlupáč a v tlamě držel zajice. Vypadá to, že si postava našla věrného a užitečného přítele.'),
    ('encounter.w.27.fail', 'en', 'The dog snapped at the offered meat and the character screamed in pain. It managed to get the hand off the muzzle but at a cost of a nasty wound. Ungrateful beast! Before the character managed to muster any retaliation, the dog ran off to the nearby bushes. The character used its shirt as a bandage and hoped the bite would not get infected.'),
    ('encounter.w.27.fail', 'cs', 'Pes se po mase vrhl a spolu s ním s zakousl i do ruky. Postava vyjekla a ucukla, ale ošklivé tržné ráně se nevyhla. Nevděčná bestie! Než se ale postava zmohla na jakoukoliv odplatu, byl pes pryč. Postava si ránu alespoň provizorně zafačovala a doufala, že se do ní nedostala infekce.'),
    ('encounter.w.28.desc', 'en', 'The character was trudging through deep snow, trying to find at least something of value in the freezing weather when the ground gave way under its feet. Despite the effort, the character disappeared underground. After a few minutes of trying to get back up, the character gave up and squeezed through the narrow passage leading to a much bigger cave. After several minutes of walking, the character spotted 12 elderly men standing around an extinguished fireplace. The old men were not at all surprised to see the character and asked for help with lighting the fire.'),
    ('encounter.w.28.desc', 'cs', 'Postava během třeskutých mrazů a hlubokého sněhu prozkoumávala vrcholek skalnatého kopce, když se pod ní začaly drolit kameny. Postavě se podařilo uskočit, ale sypající se suť byla nepřekonatelný problém a trochu potlučená se propadla pod zem. Pád naštěstí postavě nezpůsobil žádná zranění, nicméně vylézt nahoru nově vzniklým otvorem se postavě nedařilo. Začala tedy prozkoumávat podzemní prostoru a zjistila, že se může protáhnout skulinou do většího skalního dómu. Na konci dómu postava uviděla světlo značící povrch. V dómu kolem vyhaslého ohniště seděla skupina velmi starých mužů. Všichni třímali berle a jeden měl dokonce na hlavě korunu. Kolik jich tu je? 12? Tak to je pohoda, pomyslela si postava. Staříci nevypadali vůbec překvapeně, že postavu vidí, a hned ji poprosili o pomoc s rozděláním ohně.'),
    ('encounter.w.28.succ', 'en', 'The character managed to light the fire (while humming C`mon baby light my fire...) which pleased the old men immensely. As a thanks, the character received freshly collected strawberries...'),
    ('encounter.w.28.succ', 'cs', 'Postavě se velmi rychle podařilo rozdělat oheň, což staříky velmi potěšilo. Jeden z nich dokonce postavě věnoval nějaké jahody. Počkat… jahody, teď v zimě? '),
    ('encounter.w.28.fail', 'en', 'Despite the effort, the character did not manage to light their fire. The old men grew more and more angry till they had lost their temper and chased the character out with their sticks and some rotten tomatoes. The character was extremely embarrassed to have failed such a basic survival skill.'),
    ('encounter.w.28.fail', 'cs', 'Ani přes snahu se postavě nepodařilo oheň rozdělat. Staříci nejprve nervózně pokašlávali, poté začali postavu pošťuchovat berlemi. Když se postava s rozpačitým úsměvem omlouvala, že nezvládla ani tu nejzákladnější dovednost, vyhnali ji staříci holemi a na cestu na ni naházeli ještě shnilá rajčata… kdo ví, kde je v tom nečasu vzali. Možná by situace dopadla lépe, kdyby si postava při rozdělávání ohně nepobrukovala hitovku “Starejte se o své věci, usmolení staří dědci, starejte se o sebe, ať vás huba nezebe.“'),
    ('encounter.w.29.desc', 'en', 'The corrupted City Hall sends its armed patrols from the city to the Wasteland. The declared goal is to keep order throughout the area. Usually, however, they tend to look for a lonely poor man and blame him for a fictional sin, such as insulting the divine Atom by inappropriate behavior. Of course, they are not concerned with morality, but with an interesting fine, or at least a bribe.'),
    ('encounter.w.29.desc', 'cs', 'Zkorumpovaná Radnice z města posílá svoje ozbrojené hlídky i do Pustiny. Deklarovaným cílem je držet pořádek v celé oblasti. Obvykle si ale spíš vyhlédnou osamělého chudáka a vyčtou mu nějaký smyšlený hřích, jako například urážku boha Atoma nevhodným chováním. Samozřejmě jim nejde o mravnost, ale o inkaso zajímavé pokuty, nebo alespoň úplatku. '),
    ('encounter.w.29.succ', 'en', 'The character noticed the City Hall squad from distance and preferred to get off the road to a nearby bush. As the patrol approached, the character even secretly overheard a conversation between the armed units. It is said that the City Hall is worried about its position in Withergate therefore it relies too much on the support of the church of Atom, named Malleus Maleficarum. This information will be useful - it may not be so dangerous to play a game of power in these days…'),
    ('encounter.w.29.succ', 'cs', 'Postava si již z dálky všimla radniční bojůvky a raději slezla z cesty do blízkého mlází. Když se hlídka přiblížila, dokonce potají vyslechla rozhovor mezi ozbrojenci. Prý se Radnice bojí o svoje pozice ve Withergate a až příliš spoléhá na podporu Atomovy církve jménem Malleus Maleficarum. Tyto informace se budou hodit - možná nemusí být v těchto dnech zas tak nebezpečné rozehrát vyšší hru o moc… '),
    ('encounter.w.29.fail', 'en', '”You there! Aren’t you doing something suspicious there? And what are you even wearing? You really don`t know, that red is the color of the Atom’s cassock, and therefore praiseworthy, and that black is the color of the City Hall military uniform, and therefore also praiseworthy ... but that red AND black together are the symbol of the cult of the mad god Rarraq!? Or revolution and chaos!? Don’t you know? I didn`t know it before either... but you will be fined for this ignorance now. Pay, or we will take the caps by ourselves, you blasphemer!”'),
    ('encounter.w.29.fail', 'cs', '“Hele ty tam, že ty tady děláš něco podezřelého? A co to vůbec máš na sobě? Což nevíš, že červená je barva Atomovy sutany, a tedy chvályhodná, a černá je barva radniční vojenské uniformy, a tedy také chvályhodná... červená a černá dohromady je ale symbolem kultu šíleného Rarraqa! Nebo taky revoluce a chaosu! To nevíš? Já to také dřív nevěděl... ale ty za to teď vysol pokutu. Koukej platit, nebo tě obereme sami, blazfemiku!”'),
    ('encounter.w.30.desc', 'en', 'The usual odors noticeable in the Wasteland are somewhere on the scale between a suffocating burning smell and decay. It was a welcome surprise that the character catched a wonderful smell of a roasted non-mutated meat. The source was produced by a cook who was preparing the best meal in the world in a cauldron on the fire in front of his home. The arrival of the character did not interest him at all. “Will you bring me origanum majorana from the garden over there?”'),
    ('encounter.w.30.desc', 'cs', 'Obvyklé pachové vjemy zaznamenatelné v Pustině jsou někde na škále mezi dusivou spáleninou a hnilobným rozkladem. O to více je překvapující, když vás do nosu praští nádherná vůně pečeného nezmutovaného masa. Postava šla rovnou za nosem. Zdroj vydávalo nějaké děcko v roušce, které v kotli na ohni před svým obydlím právě připravovalo ten nejlepší pokrm na světě, a příchodem postavy se vůbec nenechalo rozhodit. “Podáš mi tamhle ze zahrádky origanum majorana?”'),
    ('encounter.w.30.succ', 'en', 'Although the character brought ordinary marjoram, she gained considerable respect for her knowledge of herbs. The chef invited the character to lunch. While eating, he talked about his life as a former culinary star: “You know, the only thing you really needed a few years before was a pure culinary. Or maybe the decoration. I mean, the decoration of the food, of course. But then they changed the rules considerably in all the cooking competitions. You won`t get many points for sole culination or decoration today, you actually have to keep collecting and junksmithering, or at least boasting a little bit.” The character was quite lost in the cook’s life story, but apparently a great life wisdom was hidden in it. But most importantly, the character brought home a lot of supplies.'),
    ('encounter.w.30.succ', 'cs', 'Postava sice přinesla obyčejnou majoránku, ale svou znalostí bylin si vydobyla značný respekt. Kuchař se nakonec ukázal být mnohem starší a pozval postavu k obědu. Při jídle vyprávěl o svém životě jako dřívější kulinářské hvězdy: “Víš, dříve sis vystačil jenom s ryzím kulinářstvím, a když to nešlo, tak alespoň s dekorací jídla - ale pak ve všech soutěžích značně změnili pravidla. Dneska za to už moc bodů nenaženeš, musíš vlastně pořád něco sbírat a tepat, nebo se alespoň hrozně vychloubat.” Postava se v tom životním příběhu dost ztratila, ale zjevně v něm bylo ukryto velké životní moudro. Co je ale hlavní, přinesla si domů spoustu zásob. '),
    ('encounter.w.30.fail', 'en', 'The character searched for some original major, or at least any major,  among the herbs until the chef came to the garden alone and disgustedly expelled the character. The rest of the day was very traumatic for the character. All day long it thought only of food and so it ate all of the supplies found that day.'),
    ('encounter.w.30.fail', 'cs', 'Postava tak dlouho mezi bylinkami hledala nějakého originálního majora, až si kuchař na zahrádku došel sám a postavu znechuceně vyhnal. Zbytek dne byl pro postavu velice traumatický, celý den myslela pouze na jídlo a tak veškeré ten den nalezené zásoby projedla.'),
    ('encounter.w.31.desc', 'en', 'Bushes usually don`t attack people, do they?'),
    ('encounter.w.31.desc', 'cs', 'Křoví normálně na lidi neútočí, že ne? '),
    ('encounter.w.31.succ', 'en', 'The character in the Wasteland met nothing less than a genetically modified aggressive plant - a triffid. Something between a walking brassica, waddling sunflower and a carnivorous orchid. But most of all, it could shoot its stinger a few meters away. Fortunately, it was an extremely small specimen and therefore more cute than dangerous. Not far away from the triffid, the character found a half-bitten unfortunate man with some junk and also broken glasses. Did the plant know that if she destroys his glasses, the man would not see anything, and the blind man would be easily caught? Ugh, who knows what these plants would be able to manage if the whole Wasteland suddenly blinds!'),
    ('encounter.w.31.succ', 'cs', 'Postava v Pustině nepotkala nic menšího než geneticky modifikovanou agresivní rostlinu - triffida. Něco mezi chodící brukví, kolébající se slunečnicí a žravou orchidejí. Ale hlavně to umělo vystřelovat na několik metrů žahadlo. Naštěstí to byl extrémně malý exemplář a tedy spíš roztomilý než nebezpečný. Kousek od triffida postava nalezla napůl okousaného nešťastníka s trochou šrotu a rozbitými brýlemi. Ta kytka věděla, že když mu zničí brýle, on pak nic neuvidí, a oslepený se nechá snadno ulovit? Brr, kdo ví, co by tyhle kytky dokázaly, kdyby oslepla celá Pustina!'),
    ('encounter.w.31.fail', 'en', 'A strange bush shot the character with a few meters long stinger. The wound burned badly. As the bush quickly toddled against the character, our hero preferred to flee.'),
    ('encounter.w.31.fail', 'cs', 'Podivný keř na postavu několikrát vystřelil několik metrů dlouhé žahadlo a ošklivě ji tak popálil. Když se křoví kolebávě rozeběhlo, raději se postava rychle dala na útěk. '),
    ('encounter.w.32.desc', 'en', 'The character was carrying its daily loot of junk on a dirty road when a long-bearded horse rider in gallop suddenly appeared behind her. The character had never seen anyone ride so fast and so quietly - the rider was barely audible. But maybe it is normal, after all, you will hardly meet a rider today - the horses are too delicious and nutritious for such purposes. The character managed to dodge at the last moment, but all its junk fell down and broke into unusable pieces. The character could shout just a few swear words addressed to a long gray cloak fluttering behind a horse, quickly disappearing beyond the horizon.'),
    ('encounter.w.32.desc', 'cs', 'Postava si spokojeně štrádovala po cestě a nesla si svůj denní úlovek šrotu, když se za ní náhle objevil dlouhovousý jezdec na koni v plném trysku. Postava ještě nikdy neviděla nikoho jet tak rychle a zároveň tak tiše - vždyť skoro nebyl slyšet. Ale možná je to normální, ostatně dnes už jezdce jen tak nepotkáte. Na takové využití jsou koně přeci příliš chutní a výživní. Postava taktak stihla uskočit, veškerý její šrot se ale vysypal a při pádu rozlámal na nepoužitelné kousky. Postava se zmohla akorát na pár nadávek adresovaných dlouhému šedému plášti vlajícímu za koněm, který rychle mizel za horizontem. '),
    ('encounter.w.32.succ', 'en', 'A few minutes later the character noticed a place where the rider had to get out of the road. Carefully, the character followed the footsteps deeper into the forest, reaching a clearing where about ten horses grazed freely. So much food! But before the character could become a butchering thief of horses, it heard a kind voice: "I actually owe you an apology for the collision on the road. Once you have found us, you deserve to know more about us. Did you think that forests simply grow in the Wasteland, and that there is so much life in them? No! That is the result of the constant work of us, druids! ” The road pirate explained to the character several surprising secrets of nature, but then politely expelled the character from the druid meeting and asked for discreteness. Man, that was amazing - this will be a great story in the pub!'),
    ('encounter.w.32.succ', 'cs', 'O něco později postava zaznamenala místo, kde jezdec musel sjet z cesty. Postava opatrně pokračovala po stopách hlouběji do lesa, až došla na paseku, kde se volně páslo zhruba deset koní. Tolik jídla! Než se však postava stihla stát řeznickým zlodějem koní, ozval se za ní vlídný hlas: “Tobě vlastně dlužím omluvu za tu srážku na cestě. Když už jsi nás našel, zasloužíš si o nás vědět více. Ty sis myslel, že v Pustině tu a tam rostou lesy jen tak, a že v lesích je tolik života samo od sebe? Omyl, to je výsledek soustavné práce nás, druidů!” Pirát silnic postavě vysvětlil několik překvapivých tajemství přírody, poté ji však taktně ze setkání druidů vykázal a požádal o mlčenlivost. Tak tohle bylo ohromující - to se to bude vyprávět v hospodě! '),
    ('encounter.w.32.fail', 'en', 'The character could not recover the destroyed junk, so in the evening the character returned to the clan base empty-handed.'),
    ('encounter.w.32.fail', 'cs', 'Zničený denní úlovek se už postavě nepodařilo obnovit, a tak se večer vrátila do klanu s prázdnou.'),
    ('encounter.w.33.desc', 'en', 'The character was just crossing a meadow and heard a click. Slowly, the character looked down and realised it stepped into a trap. At least it did not lift the weight of the foot so the trap did not go off. Sweating, the character decided to give it a try and remove the foot as fast as possible.'),
    ('encounter.w.33.desc', 'cs', 'Postava si vykračovala rozkvetlou loukou, když uslyšela cvaknutí. Opatrně se podívala dolů a zjistila, že stojí přímo v pasti na medvěda. Naštěstí ze zachycené nohy zatím nepřenesla váhu. Strachy zpocená postava seznala, že není jiná možnost než nohu co nejrychleji odtáhnout a doufat...'),
    ('encounter.w.33.succ', 'en', 'Phew! That was a close one... empowered by this near-foot-losing experience, the character sat down and fiddled a bit with the device...and managed to take it with him.'),
    ('encounter.w.33.succ', 'cs', 'Ufff. Zuby pasti scvakly naprázdno několik setin vteřiny co postava zvedla nohu. Postava coby zkušený harcovník po Pustině past samozřejmě nenechala jen tak ležet a po chvilce hraní s mechanismem si ji odnášela na místo, kde ji použije na jinou kořist.'),
    ('encounter.w.33.fail', 'en', 'That wasn`t fast enough! The fangs of the trap dug deep into the flesh. The character nearly passed away from the pain but somehow managed to find the power to open the jaws of the trap and pull the mangled leg out. It took the character plenty of time to get back to the base and would definitely take several days before the calf recovers.'),
    ('encounter.w.33.fail', 'cs', 'Cvak! Postava rozhodně nebyla dost rychlá a zuby pasti se zakously do masa. Postava sice málem omdlela bolestí, ale dokázala čelisti pasti roztáhnout alespoň natolik, aby nohu vytáhla. Postavě pak trvalo celý den, než se dostala domů a bude jistě trvat několik dní, než se vzpamatuje.'),
    ('encounter.w.34.desc', 'en', 'The character carefully approached an overturned wagon on the road. There was no need for caution, it was already looted and only the corpses of the merchant and his daughter were lying nearby. What could`ve happened?'),
    ('encounter.w.34.desc', 'cs', 'Postava se opatrně přiblížila k převrácenému vozu, který spatřila na cestě, ale našla jej jen vyrabovaný a poblíž podřezaného kupce a jeho dceru. Co se tady asi stalo?'),
    ('encounter.w.34.succ', 'en', 'The character analysed the crime scene cautiously. It was immediately obvious that one of the murderers jumped behind the merchant from a branch hanging over the road. The character also discovered there were at least two other bandits on either side of the road where the grass was lying low. Nearby, an empty glass and a couple of bones assured the character that the murderers enjoyed their success way too early. Sure enough, there were footprints in the mud that led the character to the bandit`s den where it discovered all of them in a comatose state caused by a pile of empty bottles. The character swiftly went through the stolen stuff and took the priciest item. It felt good... after all, stealing from a thief isn`t stealing, right?'),
    ('encounter.w.34.succ', 'cs', 'Postava bryskně analyzovala místo činu, zjistila, že jeden lapka seskočil na kozlík ze stromu nad cestou a minimálně další dva se ukrývali ve zválené trávě po okrajích cesty. Z odhozené láhve a okousaných kostí nedaleko usoudila, že lapkové začali svůj lup oslavovat značně brzo a na základě zlámaných větviček a stop v blátě se opatrně přiblížila k jejich příbytku. Všichni tři lapkové ale v notně podroušeném stavu pochrupovali kolem. Postava si z lupu odnesla to nejcennější, co našla, a zvolila rychlý ústup, než se někdo z lapků probudí. Morálně se postava cítila rovněž na výši, okradla přece zloděje a vrahy.'),
    ('encounter.w.34.fail', 'en', 'The character pondered the crime scene for a while and decided the murder weapon was a crossbow. Unfortunately, the victims didn''t have any holes in them and the arrows were also in short supply at the spot. They must''ve been strangled then... though it seemed unusual that a rope would cause that sharp slit on their throat. In the end, since nothing was really plausible, the character concluded a double suicide have appeared here and contended with solving the crime, decided to move onwards.'),
    ('encounter.w.34.fail', 'cs', 'Hmmm... postava se zamyslela, ale ne a ne tomu přijít na kloub, chvilku si myslela, že oběti někdo zastřelil kuší, ale nenašla v nich ani šipky ani díry. Zvažovala i uškrcení, ale nebyla si jistá, jestli by se špagát dokázal proříznout člověku hrdlo od ucha k uchu. Nakonec případ tedy uzavřela jako dvojnásobnou sebevraždu a šla spokojeně dál s dobrým pocitem, že spravedlnosti bylo učiněno zadost.'),
    -- CITY
    ('encounter.c.1.desc', 'en', 'Character was attacked by a mutated cat while roaming through the abandoned streets of the city ruins.'),
    ('encounter.c.1.desc', 'cs', 'Postavu při toulkách opuštěnými ulicemi napadla zmutovaná kočka. Vřískající a škrábající sakra rychlé malé černé cosi.'),
    ('encounter.c.1.succ', 'en', 'Character managed to kill the feline and collected some junk on the way back home.'),
    ('encounter.c.1.succ', 'cs', 'Postavě se podařilo tu kočkosvini zabít a posbírat cestou domů trochu šrotu.'),
    ('encounter.c.1.fail', 'en', 'Character was wounded during the combat.'),
    ('encounter.c.1.fail', 'cs', 'Postava si ze souboje odnesla několik hrozivě vypadajících škrábanců.'),
    ('encounter.c.2.desc', 'en', 'Character was attacked by a mutated dog while roaming through the abandoned streets of the city ruins.'),
    ('encounter.c.2.desc', 'cs', 'Postavu při toulkách opuštěnými ulicemi napadl zmutovaný pes. Má sice jen jednu hlavu, ta je ale odpudivá za dvě.'),
    ('encounter.c.2.succ', 'en', 'Character managed to kill the beast and sell it in the city market for meat.'),
    ('encounter.c.2.succ', 'cs', 'Postavě se podařilo obludu zabít a prodat ji na blízkém trhu na maso. Místní zřejmě ocení novou mutaci svých chuťových pohárků.'),
    ('encounter.c.2.fail', 'en', 'Character was wounded during the combat. The wound got infected and required buing a desinfection in the city market.'),
    ('encounter.c.2.fail', 'cs', 'Netvor postavu ošklivě pokousal. Rána se rychle zanítila a tak bylo nezbytné na městském trhu koupit předraženou dezinfekci.'),
    ('encounter.c.3.desc', 'en', 'Character met a junkie while searching in the streets of the city. The junkie asked for some caps and, after being rejected, pulled out a knife.'),
    ('encounter.c.3.desc', 'cs', 'Při průzkumu města postava potkala pochybnou smažku. Feťák obhrouble požádal o několika zátek a poté, co je nedostal, vytáhl nůž.'),
    ('encounter.c.3.succ', 'en', 'Character overcame the attacker and took the gear he was carrying.'),
    ('encounter.c.3.succ', 'cs', 'Postava porazila útočníka a obrala jej o vybavení, které měl u sebe.'),
    ('encounter.c.3.fail', 'en', 'Character was wounded during the combat. The wound got infected and required buing a desinfection in the city market.'),
    ('encounter.c.3.fail', 'cs', 'Feťák postavu pořezal nožem. Rána se zanítila a tak bylo nutné na městském trhu koupit dezinfekci.'),
    ('encounter.c.4.desc', 'en', 'A priest in a torn cape was met while roaming the city streets. The priest asked if our hero believed in the cult of the Atom.'),
    ('encounter.c.4.desc', 'cs', 'V průběhu toulky ulicemi postavu oslovil kněz v otrhané kápi. Otevřeně se zeptal, zda postava uznává Atomův kult.'),
    ('encounter.c.4.succ', 'en', 'Character nodded, knowing that rejection might turn out to be dangerous when speaking with fanatics. The priest said a blessing and some some junk was collected on the way back.'),
    ('encounter.c.4.succ', 'cs', 'Postava raději přikývla, protože fanatici mohou být nebezpeční. Spokojený kněz postavě požehnal. Vzápětí nalezla několik kusů cenného šrotu. Náhoda?'),
    ('encounter.c.4.fail', 'en', 'Character did not know anything about such cult so cursed and tried to leave the strange man behind. Maybe it was just a strange coincidence, but nothing was found that day.'),
    ('encounter.c.4.fail', 'cs', 'Postava nikoho takového neznala a tak raději cizince rychle opustila. Kněz zřejmě nebyl potěšen a postavu z dálky proklel. Možná je to jen náhoda, ale ten den postava nenalezla vůbec nic cenného ani zajímavého.'),
    ('encounter.c.5.desc', 'en', 'This house looks pretty unexceptional, but for some reason is guarded by two guards in torn capes. We need everything which is inside! What about to wait, maybe we can sneak inside...'),
    ('encounter.c.5.desc', 'cs', 'Ten dům není na pohled ničím zajímavý, ale z nějakého důvodu u něj neustále stojí dvojice strážných v otrhaných kápích. Ať je uvnitř cokoliv, určitě to potřebujeme! Co takhle tu chvíli počkat, třeba půjde jít dovnitř bez boje...'),
    ('encounter.c.5.succ', 'en', 'Hiding in shadows payed off! The character found out that the building is probably a vault of worshippers of Atom and that everyone who knows the password “IDDQD” will be allowed to enter. The guard respectfully greeted the character and permitted to take a few interesting belongings.'),
    ('encounter.c.5.succ', 'cs', 'Vyčkávání ve stínu se vyplatilo. Postava zjistila, že se zřejmě jedná o vault uctívačů Atoma a dovnitř se kdokoliv dostane pomocí hesla “IDDQD”. Stráž postavu následně uctivě pozdravila a dovolila vynést z vaultu několik zajímavých věcí.'),
    ('encounter.c.5.fail', 'en', '”Hey, you!” The individuals in capes were not friendly at all when they spotted the character in a shadow. All of sudden, there was really a lot of them, so they beated the character slightly and took his caps.'),
    ('encounter.c.5.fail', 'cs', '“Co tady očumuješ?” Otrhanci v kápích nebyli moc přátelští, když si všimli postavy ve stínu. Z okolí se jich seběhlo hodně, tudíž postavě nadělili zasloužený průplesk a sebrali jí trochu zátek.'),
    ('encounter.c.6.desc', 'en', 'A new tavern in the city? And it’s called Cantina Band... Well, it does not look much safe in there. Some strange musicians are playing inside and all the people look pretty happy (and stoned as well), but some other guys are evidently picking a quarrel. Look, somebody get killed right now over there! Oh, here it comes, a couple of really disgusting men told you they hate you. '),
    ('encounter.c.6.desc', 'cs', 'Postava natrefila na nově otevřenou putyku jménem “Cantina band”. Nevypadá to jako moc bezpečné místo - sice v pozadí hrají muzikanti veselé melodie na divné dechové nástroje a všude se to hemží šťastnými existencemi popíjejícími zjevně omamné substance, ale na některých místech probíhají i hádky a vyřizování účtů. “Hej, mutanty neobsluhujeme!”, vyhání barman jednoho z hostů, který vypadá jen o něco divněji než ostatní. Postava si objedná na baru pití a doufá, že zjistí nějaké zajímavé informace. Zatímco čeká na baru, přihlíží, jak opodál někdo někoho mezi řečí zastřelil. Postavě náhle zaklepe na rameno nějaký hnusák: “Tady muj kámo tě nemá rád. A já tě taky nemám rád!” Ty dva asi nepůjde jen tak odbýt...'),
    ('encounter.c.6.succ', 'en', 'The only way how to deal with them was cutting off a hand of one of the men. And it worked, the second one ran away! After this incident the character fitted into local society quite easily, and found out interesting information. Although, it seems that it will be better not to visit this place ever again. '),
    ('encounter.c.6.succ', 'cs', 'Nezbývá, než jednomu z hnusáků useknout ruku na stole se povalující sekáčkem na maso a druhého šokem zahnat. Díky tomuto vystoupení postava snadno zapadla mezi místní kolorit, díky čemuž zjistila zajímavé informace. Přesto má postava pocit, že sem už raději nikdy nepůjde. '),
    ('encounter.c.6.fail', 'en', 'The couple gave the character a few slaps and threw him/her out of the tavern.'),
    ('encounter.c.6.fail', 'cs', 'Postava dostala pár facek a nechala se vypoklonkovat z hospody. Tak tady tu michelinskou hvězdu asi nedostanou...'),
    ('encounter.c.7.desc', 'en', 'There was a meeting of locals in the city, probably a members of Atom’s cult. They discussed news in the city. It seemed that they really hate Wastelanders, because we are only stealing their food and junk (which is actually correct). But it would be useful to find out what are these people planning in Withergate.'),
    ('encounter.c.7.desc', 'cs', 'Na volném prostranství se začalo shromažďovat větší množství místních obyvatel. Dle oděvů se jedná o přívržence Atomova náboženství a zřejmě rokovali o novinkách ve městě. Sice ti lidé okolo evidentně neskrývají své antipatie k obyvatelům Pustiny, protože jim prý pořád jen krademe šrot a jídlo (jo, to mají pravdu), ale možná by se nám hodilo vědět, co nového plánují ve Withergate.'),
    ('encounter.c.7.succ', 'en', 'The character successfully infiltrated the meeting and found out interesting information.'),
    ('encounter.c.7.succ', 'cs', 'Postava se snažila vypadat co nejvíce nenápadně a nijak na sebe neupozorňovat. Vhodně položenými dotazy zjistila postava zajímavé informace. '),
    ('encounter.c.7.fail', 'en', 'It was not probably the best idea to try a public defence of our clan. The character was vehemently recommended to leave the city. Regrettably, the mob instead of strong arguments used strong baseball bats.'),
    ('encounter.c.7.fail', 'cs', 'Asi nebyl dobrý nápad tady zkusit veřejnou obhajobu našeho klanu. Postavě bylo důrazně doporučeno, aby táhla zpátky do pustiny. Bohužel dav namísto pádných argumentů použil pádné hole.'),
    ('encounter.c.8.desc', 'en', 'One of the houses in the city looked quited unpillaged...'),
    ('encounter.c.8.desc', 'cs', 'Ten dům vypadal docela nevypleněně a skoro stabilně...'),
    ('encounter.c.8.succ', 'en', 'But it can collapse anytime soon! The character searched the house carefully and found a few valuables that haven’t been discovered by the marauders yet.'),
    ('encounter.c.8.succ', 'cs', 'Při bližším pohledu se ukázalo, že stačí trochu silněji proběhnout po schodech a zřítí se celá střecha. Postava dům velice opatrně prohledala a našla několik pěkných cenností, které zatím odolaly ostatním plenitelům.'),
    ('encounter.c.8.fail', 'en', 'It was not stable at all. The building collapsed when the character was inside looking for valuables. The character was lucky and escaped without injury. But outside was an officer who fined the character, because it is probably against the law when you destroy somebody’s building.'),
    ('encounter.c.8.fail', 'cs', 'Ale moc stabilní nebyl. Ve snaze najít pár cenností postava poškodila statiku domu a zřítila se celá střecha. Postava se naštěstí dostala ven bez větších zranění. Venku se však seběhlo několik lidí. Jeden z nich, oblečený do uniformy, postavu ihned zkásl o nějakou vylhanou pokutu, prý za rozkrádání majetku ve Withergateském vlastnictví.'),
    ('encounter.c.9.desc', 'en', 'The locals are playing some sort of a game in the streets. One man moves with three shells and the player has to guess the one hiding a small cogwheel. Left, middle or right?'),
    ('encounter.c.9.desc', 'cs', 'Místní obyvatelé, když zrovna nebijou cizince, tráví svůj volný čas různě. Někdy i podivnými hrami na ulici. Tady například nějaký přátelský pán nabízí zábavu a k tomu i snadno vydělané zátky. Stačí uhodnout, pod kterou ze tří nádob od vyjetého oleje schoval malé ozubené kolo. “Vidíte kolečko? Dávám ho sem. Teď kelímky zamíchám. Doleva, doprava, zpátky, do středu, zase doprava, tak… Vidíte kolečko? A raději ještě zamíchám. Tak, a kdepak je kolečko teď?” Hromada čumilů vsází a pokládá své zátky k jedné ze tří nádob. “Á, tady bylo kolečko! Gratuluji, madam!” Vítězové se radují, poražení nadávají, ale jdou si vsadit znovu. “Vy tam, pojďte si taky zahrát!”'),
    ('encounter.c.9.succ', 'en', 'The character know that the trick of the game is somewhere else… Yes, there is a accomplice of the shellgamer who steals in the mob... The character publicly accused the shellgamer of cheating. The bystanding officers in uniform was interested in this, and quickly sentenced the cheaters to public beating. The officers also confiscated their property a part of it gave back to the cheated persons. Also the character received a part of this dirty money. '),
    ('encounter.c.9.succ', 'cs', 'Postava celkem snadno několikrát vyhrávala, až podezřele snadno. Postava si proto více začala všímat svého okolí, až zahlédla skořápkářova komplice, který v davu nerušeně okrádal přihlížející. Skořápkář to asi zaznamenal, protože se začal rychle pakovat. “Á, tamhle jdou oficíři, rychle pryč!” Postava však reagovala rázně, rychle obvinila skořápkáře z podvodu a společně s několika přihlížejícími povedenou dvojici zajistili. Oficíři v uniformě na místě povedenou dvojici zbili a zkonfiskovali jim veškerý majetek. Část z něj rozdali lidu. Postava si za svou činnost vysloužila lví podíl. Instantní spravedlnost v praxi!'),
    ('encounter.c.9.fail', 'en', 'The character was succesful at the beginning, but later was not lucky at all. The character lost a few caps. But later the character found out there was probably a thief in the mob, because some other caps were missing too!'),
    ('encounter.c.9.fail', 'cs', 'Postava několikrát snadno vyhrála, ale pak se štěstí podezřele otočilo. Skořápkář se následně hry nabažil, rychle ji ukončil a zmizel v uličkách města. Cestou domů postava zjistila, že nejen že prohrála několik zátek, ale další zátky jí někdo v davu ukradl. Zbytek dne se postava snažila podvodníky najít, ale neúspěšně.'),
    ('encounter.c.10.desc', 'en', 'There was a sermon of Atom’s priest in the streets. It seems like Atom sets Withergate people against the Wastelanders. “Look, they are between us! One of the Wastelanders is standing there, ready for stealing our food and junk! Hey, yes you, why are you here?” The character started to defense...'),
    ('encounter.c.10.desc', 'cs', 'V jedné z ulic se několik lidí začalo připravovat na jakýsi rituál. Šlo zřejmě o kázání vedené knězem boha Atoma. Obyvatelé města nevypadají moc přátelsky, naopak to vypadá, že je Atom poštvává proti lidem z Pustiny. Původně to mělo být na téma “Atom jsi a v atom se obrátíš", ale pak se to nějak zvrhlo… “Podívejte, jsou už i mezi námi! Tamhle jeden stojí, připraven ukrást naše zásoby a rozebrat naše zásoby šrotu! Jo, ty, s tebou mluvím, co tady chceš, proč jsi přišel do města?” Postava se nadechla a zkusila se obhájit...'),
    ('encounter.c.10.succ', 'en', 'And with a huge success! The priest lost the favour of the crowd and changed quickly the topic of the sermon. One of the other Wastelanders in the crowd whispered thanks and gave the character a small reward for being brave.'),
    ('encounter.c.10.succ', 'cs', '...a byla úspěšná! Kněz ztratil přízeň davu a tak se raději vrátil k původnímu tématu. Jeden obyvatel Pustiny šeptem postavě velice poděkoval za to, že obhájila venkovní klany. “Je dobře, že ses nás všech zastal a promluvil za nás! Tady máš malou odměnu za svou odvahu.”'),
    ('encounter.c.10.fail', 'en', 'Without chance. The character wasn’t lynched, but it had to pay compulsory indulgences.'),
    ('encounter.c.10.fail', 'cs', 'Bez šance. Nedošlo sice k lynčování, ale postava nakonec musela “dobrovolně” věnovat milodary přímo do náruče církve.'),
    ('encounter.c.11.desc', 'en', 'Our character was stopped in a dark alley by two muscular and drunk vagabonds - probably fans of a local jugger club. One of them greeted our hero with a friendly line "What are you looking at?". The other, probably clouded by the amount of alcohol in his blood, seconded: "Wanker!". Before the character managed to reply, they attacked.'),
    ('encounter.c.11.desc', 'cs', 'Dva notně podnapilí svalnatí týpci, od pohledu zřejmě fanoušci místního jugger klubu, v potemnělé uličce s postavou zahájili rozhovor přátelskou frází “Co tak blbě čumíš?” Druhý z nich byl vzhledem ke svému mozkovému a alkoholovému deficitu schopen říct jen “Pyčo.” Asi nějaká modlitba. Fanatici jsou vždy nebezpeční - na repliku nečekali a ihned zaútočili.'),
    ('encounter.c.11.succ', 'en', 'Probably thanks to the alcohol, our hero managed to overcome the attackers and taught them some manners. Furthermore, the drunkies left some caps behind them during their clumsy retreat.'),
    ('encounter.c.11.succ', 'cs', 'Byli vlastně docela hodně podnapilí, tudíž jim postava poskytla důkladnou lekci o nežádoucích účincích alkoholu a nutnosti prevence. Zároveň se opilci nechali obrat o několik zátek. '),
    ('encounter.c.11.fail', 'en', 'Fueled by booze, the vagabonds overcame our character.'),
    ('encounter.c.11.fail', 'cs', 'Posilněni alkoholem postavu přeprali a způsobili jí nepříjemná zranění.'),
    ('encounter.c.12.desc', 'cs', 'Postava se rozhodla prohledat opuštěný dům, na pohled však bylo zřejmé, že ještě před krátkou chvílí zde někdo žil. Musel se někde schovat, spolu se svými poklady… '),
    ('encounter.c.12.succ', 'en', 'One of the planks on the floor could be removed quite easily. Underneath, a little underground hiding was discovered - and a very frightened old man. He was holding his most valuable belongings on his chest and kept murmuring something in gibberish. Clearly a lunatic. Our character took his stuff without any resistance and left.'),
    ('encounter.c.12.succ', 'cs', 'Jedno z prken podlahy šlo snadno odstranit, čímž se objevila malá podzemní skrýš. V ní byl sice obrovský, ale k smrti vyděšený chlap. Na prsou svíral svoje největší cennosti a pateticky mumlal “... tak jsi přeci přišel… po všech těch letech, našli jste mě… já nechtěl… muselo se to stát...” Zřejmě blázen. Postava mu vytrhla z neodporujících rukou zajímavý předmět a rychle utekla.'),
    ('encounter.c.12.fail', 'en', 'Someone smashed our character in the head during the house search. It is not clear whether it was the house resident or some other hobo. Our character fleed the house without looking back. The only result of this trip was a painful head injury.'),
    ('encounter.c.12.fail', 'cs', 'Po dlouhém prohledávání postavu někdo praštil do hlavy velkým dubovým prknem. Ať už to byl obyvatel domu nebo jiný vandrák, uměl dát ránu - postava utekla a spokojila se ten den s neúspěchem a bolestí hlavy.'),
    ('encounter.c.13.desc', 'en', 'A group of people was repairing a fallen wall on a small city square. Probably not the best craftsmen in the city - the wall was leaning to one side and it was just a question of time before it falls again. Our character was in a good spirit so offered help with the reconstruction.'),
    ('encounter.c.13.desc', 'cs', 'Na náměstí skupina lidí opravovala zřícenou zeď. Moc jim to nešlo. Zeď se jim prohýbala na jednu stranu a hrozila zhroucením. Postava měla družnou náladu: “Čemu nerozumíte, pánové? Máte štěstí, že jdu zrovna kolem!” S těmi slovy použila část svého dnes nalezeného šrotu a nainstruovala ostatní, jak a kde postavit sofistikované podpěry.'),
    ('encounter.c.13.succ', 'en', 'After couple of hours, the wall was fixed. The people thanked our hero and offered some caps for the hard work.'),
    ('encounter.c.13.succ', 'cs', 'Stavitelství ve městě zřejmě není na takové úrovni, když jim o tom můžeme přednášet my. Samozvaní zedníci za cennou lekci rádi zaplatili několik zátek.'),
    ('encounter.c.13.fail', 'en', 'The wall crumbled to the grounds as soon as our character touched it. Some people were injured during the fall and our hero decided to leave the scene before any accusations could be raised.'),
    ('encounter.c.13.fail', 'cs', 'Zeď se neslavně zřítila a zavalila samozvané zedníky i s podpěrami. Postava se rozhodla, že nebude čekat, až se vyhrabou zpod cihel, a zvolila taktický ústup. Withergate zůstává dlužno našemu klanu několik šrotu.'),
    ('encounter.c.14.desc', 'en', 'Misty evening, wolf howl, an old-time cemetery, and a bunch of creepy figures in the moonlight. One of them declaimed to the others: ”Everything is opposed to nothing by virtue of it Being or by its direct Beingness. Something which was in no way associated with Being - rather not in any way manifesting Beingness - would not be anything, because it is nothing!“'),
    ('encounter.c.14.desc', 'cs', 'Večer za tmy padla mlha. Postava trochu zabloudila, až došla na starodávný hřbitov na kraji města. Ve svitu měsíce, za vzdáleného vytí vlků... spatřila děsivou seanci. Jeden tvor zřejmě něco přednášel ostatním: “Všechno se totiž liší od nic jen tím, že má bytí nebo je jsoucno. Něco, co by se vůbec netýkalo bytí - tedy by se žádným způsobem nejevilo jako jsoucno - by nebylo něčím, nýbrž právě ničím!” Ostatní mu zblble přikyvují.'),
    ('encounter.c.14.succ', 'en', 'Finally! Our character found someone on the same spiritual level - intellectuals! Just one evening was enough to gain some deep knowledge. It is unfortunate and others from our clan will probably not understand any of it and will be only interested in the news about life in Withergate.'),
    ('encounter.c.14.succ', 'cs', 'Postava se zaradovala, protože konečně našla tvory sobě podobné - intelektuály. Doposud se zdálo, že je to pouhý mýtus. Nyní však postava získala jediným rozhovorem o dialektice obrovského vědění. Škoda, že pro ostatní z klanu, kteří nedosáhli takové intelektuální výše, se toto poznání zdá jen jako jen pár informací o životě ve Withergate...'),
    ('encounter.c.14.fail', 'en', 'Oh no! Intellectuals! Our character panicked and started running recklessly towards the camp. The feeling of being pursued lasted for another hour and our hero suffered couple of injuries during the stampede.'),
    ('encounter.c.14.fail', 'cs', 'No doprdele - intelektuálové. Postava v panické hrůze prchala hlava nehlava. Měla pocit, že ji pronásledovali až domů. Tam se dostala za cenu bolestivých zranění. Fyzických i duševních. Už nikdy nepůjde v noci ven sama.'),
    ('encounter.c.15.desc', 'en', 'A group of slavetraders tried to capture the character.'),
    ('encounter.c.15.desc', 'cs', 'Postavu si jako vhodné zboží vyhlédla skupinka otrokářů. Už si vedli jiné nešťastníky, které nejspíš čeká zářivá kariéra v hutnickém průmyslu.'),
    ('encounter.c.15.succ', 'en', 'The character wounded several enemies in the fight. This helped the slavetrader’s captives to liberate themselves. The misfortunate slavers quickly ran. The liberated slaves shared interesting news about the city with the character.'),
    ('encounter.c.15.succ', 'cs', 'V souboji s nepříjemnou přesilou se postavě podařilo zranit několik nepřátel, čehož využili jejich dřívější zajatci. Otrokáři, neschopni uhasit nově zažehnutou pochodeň svobody, se dali na rychlý útěk. Osvobození otroci u sebe nic neměli, ale podělili se o několik zajímavých informací o situaci ve městě.'),
    ('encounter.c.15.fail', 'en', 'The character lost the fight, but at least wasn’t taken captive.'),
    ('encounter.c.15.fail', 'cs', 'Bylo jich příliš mnoho. Naštěstí se postava nedostala do zajetí.'),
    ('encounter.c.16.desc', 'en', 'There was a cruel fight between two bands of scoundrels in the Withergate. The outfit of all members of one group was decorated with a worn sash. The character was nearby just in the time of the battle. Only the leader of the decorated group spotted the character: “Help us, we will pay.”'),
    ('encounter.c.16.desc', 'cs', 'Postava se připletla do ulice, kde se zrovna rvaly dvě místní bandy. Otrhaní byli všichni, ale členové jedné skupiny byly ověnčeni nějakou šerpou. Kdoví, co to znamenalo. Bitva byla poměrně vyrovnaná. Velitel skupiny se šerpou se dostal do úzkých, když na něj nebezpečně dotírali dva soupeři. Rychle zvolal na postavu “Pomož mi, zaplatím!”'),
    ('encounter.c.16.succ', 'en', 'The character successfully backstabbed one of the enemies of the sash-decorated ones and easily decided the battle. The leader of the winning side gladly paid couple of caps to our hero..'),
    ('encounter.c.16.succ', 'cs', 'Postava okamžitě a nečekaně vpadla do zad bojovníkovi, který se před chvílí snažil vpadnout do zad veliteli šerpistů, a silným úderem jej vyřadila z boje. To bitvu rozhodlo a okamžitě skončila. Velitel šerpistů poté skutečně postavě zaplatil několik zátek jako odměnu.'),
    ('encounter.c.16.fail', 'en', 'Our character was not able to successfully intervene in the brawl. The sash-less group started to win decisively. The character rather quickly ran away.'),
    ('encounter.c.16.fail', 'cs', 'Postava nedokázala včas reagovat a jen se dívala, jak se velitel skupiny klátí pod úderem sekery nějakého otrhance. Během pár vteřin byli všichni se šerpou mrtví. No co, alespoň si intervencí postava neudělala zbytečné nepřátele. Přesto postava raději v obavách o svůj život rychle opustila město a do klanu nepřinesla vůbec nic cenného.'),
    ('encounter.c.17.desc', 'en', 'During a visit to the City the character was halted by a stranger wearing a cape. The stranger asked the character whether it believed in Rarraq. The character has already heard about this new cult and having experience with religious fanatics did not hesitate to say that it indeed did. This has not seem to satisfy the stranger. And do you belong to the True and Only Church of Rarraq, Our Saviour and the Scourge of Machines? Or The Church of Great Rarraq, The Punisher of the Unworthy? Or the Reformed and Reborn Church of Rarraq, Lord of the Universe? This has taken the character aback. It has never heard about any of these sects and it seemed the stranger was very intent on the answer. The character thought about it for a while...'),
    ('encounter.c.17.desc', 'cs', 'Během návštěvy Města byla postava zastavena mužem v kápi, který se jí zeptal, zda věří v Rarraqa. Postava už slyšela o této nové církvi a z opatrnosti a předchozí zkušenosti s náboženskými fanatiky pro jistotu přikývla. Muž ale zjevně nebyl uspokojen. A jsi členem Pravé a Jedinečné Církve Rarraqa, Našeho Spasitele a Ničitele Strojů? Nebo Církve Velkého Rarraqa, Trestajícího Nehodné Zástupy? Anebo snad Obrozené a Reformované Církve Rarraqa, Pána Všehomíra? To už se postava dostala do úzkých, o žádném z těchto spolků ani jednou neslyšela a zdálo se, že neznámému na odpovědi velmi záleží. Postava se zamyslela...'),
    ('encounter.c.17.succ', 'en', '...and noticed a whip tucked to the strangers belt and the scars on his forearm. The character took a deep breath and said confidently: "I believe in the only true face of Our Lord Rarraq, the strictest punisher of the unbelievers. This has pleased the stranger immensely and led to an ecstatic discourse of the fundamental aspects of the faith. It meant that the character is now able to identify a heretic just by the look of his face.'),
    ('encounter.c.17.succ', 'cs', 'A pak si všimla biče za pasem neznámého a jizev na jeho zápěstí. Nadechla se a řekla, že uznává jedinou pravou tvář Rarraqa, spravedlivě trestající hříšníky. Potěšen, že našel souvěrce, začal neznámý postavě vykládat o zásadních rozdílech mezi těmito sektami, čímž se zasloužil o to, že postava zajisté rozpozná heretika od pohledu.'),
    ('encounter.c.17.fail', 'en', '...and then took a guess. Well, uhm, the True and Only Church of course. "Heretic," exclaimed the stranger "don`t you know that their teaching is fundamentally flawed in the most essential parts?!" This was followed by an excruciatingly long lecture on the crucial aspects of the faith. The character could not keep up with such a high-level theology and was left completely confused. In fact the character was not even sure that it remembered its name.'),
    ('encounter.c.17.fail', 'cs', 'A pak to zkusila. Přece Pravá a Jedinečná Církev, to dá rozum. "Heretiku," rozkřikl se neznámý "copak nevíš, že jejich výklad učení je zásadně pomýlené v těch nejfundamentálnějších tezích?" Následovala velmi dlouhá a zapálená přednáška o významných rozdílech víry. Postava nezvládla s těmito složitými teologickými myšlenkami držet krok a odešla totálně zmatena, nejsouc si jistá ani svým jménem.'),
    ('encounter.c.18.desc', 'en', 'Even though it`s usually quite risky, the character decided to search a huge public building in the city. Through broad staircases the character managed to discover an immense room full of bookshelves making the character wonder about its original purpose. Be it as it may, it has probably been thoroughly searched through... or was it? Maybe someone more reasonable was afraid to search the uppermost shelves. It might be worth taking the risk.'),
    ('encounter.c.18.desc', 'cs', 'I když je to velký risk, postava během pátrání vlezla do velké reprezentativní budovy. Skrze mohutná schodiště se dostala až do obrovské místnosti plné regálů. Postavu přemýšlela, co asi lidem před Apokalypsou stálo za vystavování. Ať to bylo co bylo, tuhle budovu již dávno předtím někdo pořádně probral... nebo ne? Možná se někdo rozumný bál vylézt až na nejvyšší poličky regálů... ale kdo neriskuje, nic nezíská.'),
    ('encounter.c.18.succ', 'en', 'With an immense effort, the character managed to climb up the highest bookshelf being very aware of the sounds it was making and the way it tilted...and has been rewarded. An old moth-eaten book was sitting on the shelf. The character snatched it and quickly made the descent. It seemed impossible to the character that people used to have building just for the books but pre-apocalyptic times were crazy, right?'),
    ('encounter.c.18.succ', 'cs', 'Postava se za cenu nejvyššího nasazení vyškrábala až na vrchol regálu, který vydával podezřelé zvuky a značně se nakláněl, ale byla odměněna nálezem zapomenuté a značně zaprášené knihy. Opravdu měli lidé tak velké budovy jen pro knihy? Copak neměli nic jiného na práci?'),
    ('encounter.c.18.fail', 'en', 'The character started climbing oblivious of the weird sounds the bookshelf was emanating. It noticed the bookshelf was tilting as it was already falling on top of the character. The fall injured the character badly and the splinters from the shattered wood are probably still stuck everywhere... the character limped out of the building resolved never to return to this weird place.'),
    ('encounter.c.18.fail', 'cs', 'Postava se rychle pustila do lezení, nedbaje na skřípavé zvuky z regálu. Že se regál naklání si všimla až když bylo pozdě. Pád rozhodně nebyl příjemný a třísky z rozlámaného dřeva se zapíchly snad všude. Postava se odbelhala pryč s tím, že do téhle budovy se už vracet nebude.'),
    ('encounter.c.19.desc', 'en', 'While wandering the city streets the character decided to pay a visit to the Irradiated Pony - the worst watering hole in the whole Withergate where the liquor is so sharp you may end up stabbed. But if you are lucky, you might gamble enough caps to afford the bandages and a little extra. The crowd was just heating up for the main show - a former arena champion armwrestling anyone who dares challenge him. The character has had quite a few glasses already and so decided to give it a shot.'),
    ('encounter.c.19.desc', 'cs', 'Při toulkách městem se postavě zachtělo něčeho ostřejšího a tak se rozhodla stavit U Ozářeného Poníka, nechvalně známé putyce té nejhorší sebranky ve Withegate. Pálenku tu mají říznou tak, že většinou odejdeš pořezán, ale zátek si tu člověk může vydělat tolik, že mu to na obvazy bohatě vystačí. S přibývajícím večerem se rozjela ta hlavní zábava, bývalý šampion arény vyzýval ostatní hosty k páce. Nikdo ho sice zatím neporazil, ale postava v sobě měla už pěkných pár skleniček a tak se rozhodla, že to zkusí.'),
    ('encounter.c.19.succ', 'en', 'The character drunkenly fell into the chair which caused The Champ to snort. But his eyebrows shot right up when he saw the biceps of the character, strengthened by the tough everyday reality of the Wasteland. The match was a spectacle, the whole joint cheered and bets were placed on both contestants. At one time, The Champ gained the upper hand but the character managed to dig deep into his physical resources and finally prevailed. The character collected the prize and decided to leave quietly before the bookmakers could get the hold on him.'),
    ('encounter.c.19.succ', 'cs', 'Karlos se při pohledu na mírně se potácející postavu usmál. Úsměv mu ztuhl jen nepatrně, když postava obnažila svůj pustinou zocelený biceps. A pak už to začalo...už už to vypadalo, že má Karlos vyhráno, ale postava si vzpomněla na hladové kamarády v základně, na jejich živoření a na luxus těch hejsků z města... spravedlivé rozhořčení se vydralo na povrch...splň...sa...mi...senil! A mocně bouchla s Karlosovou rukou o stůl. Vítězných oslav si postava nicméně příliš neužila, neboť její vítězství naštvalo nejednoho sázkaře a pověst lokálu byla v tomto ohledu více než známá.'),
    ('encounter.c.19.fail', 'en', 'The character fell drunkenly into the chair. The Champ snorted derisively. "Let`s be done with this." he muttered. His biceps was the size of our characters thigh and the character realized that this bet might have been a mistake. The match alone was quite unspectacular...The Champ did not win straight away (probably because he didn`t want to scare off other potential contenders) but when the crowd started mocking him for not being able to finish a drunken wastelander he ended it abruptly. The character lost all its caps and was lucky nobody was betting on him otherwise he might have lost more...'),
    ('encounter.c.19.fail', 'cs', 'Postava se sotva dokolébala ke stolu, kde si šampión Karlos olejoval biceps. Postava v tu chvíli byla už tak opilá, že začala mluvit divnou řečí. "Čo to robíš, ty kokot..." Karlos se ale nenechal rozhodit: "Tak rychle, ať to máme za sebou..." Po zahájení zápasu to chvíli vypadalo nadějně, ale možná jen proto, že Karlos nemůže hned odradit případné zájemce. Když se ale začal ozývat posměch, že si neporadí ani s ožralou, zápas rázně ukončil. Postava přišla o všechny svoje zátky a mohla být ráda, že si sázkaři také přišli na své, jinak by to možná dopadlo ještě hůř.'),
    ('encounter.c.20.desc', 'en', 'Rummaging through a decrepit house in the city, the character stumbled upon a weird tube-shaped object. Upon close inspection, the character discovered it shows changing colorful patters when you look through it. It didn`t seem to have any other function (probably the reason why it was lying there) but the resourceful character decided to change it into binoculars.'),
    ('encounter.c.20.desc', 'cs', 'Při prohledávání jednoho z domů nalezla postava zvláštní tubu, která vypadalo jako dalekohled a po zatřesení vydávala zvuky. Když se skrz ní ale postava podívala, viděla jen barevné obrazce, které se měnily když otáčela horní částí tubusu. Nezdálo se, že by to bylo k nějakému užitku (proto se to zde také povalovalo bez povšimnutí), ale postavu jako správného kutila napadlo, že kdyby vysypala barevné korálky a změnila umístění a pořadí čoček, možná by z toho byl ucházející dalekohled.'),
    ('encounter.c.20.succ', 'en', 'The character managed to cut a hole into the upper part of the tube and get rid of the colorful beads, while keeping them as a means of exchange. Through careful handling the character was able to make modifications on the lens and heureca! It is probably not a microscope but it magnifies and that`s considered a victory.'),
    ('encounter.c.20.succ', 'cs', 'Postava nařízla díru do horní části, odkud vysypala korálky (třeba je ještě využije jako směnný prostředek), ve spodní části pak přeskupila čočky a upravila sklíčka...a heuréka! Není to zrovna mikroskop, ale přibližuje to vcelku slušně.'),
    ('encounter.c.20.fail', 'en', 'The character threw itself into the project but after several hours realized the beads were still stuck in the tube. Finally, the character had to admit defeat when the lens fell off the tube and shattered on the ground. It didn`t turn out to be a very productive afternoon but at least nobody will be using this (now completely broken) thing against us.'),
    ('encounter.c.20.fail', 'cs', 'Postava se pustila do díla, nicméně ani po několika hodinách usilovné práce nedokázala vysypat korálky z tuby, navíc ji při neopatrné manipulaci vypadly čočky a rozbily se...no co, promarnili jsme den, ale alespoň to nikdo nevyužije proti nám.'),
    ('encounter.c.21.desc', 'en', 'It is very difficult for the musically gifted to break through in Withergate. Right now a bunch of boys gathered on the corner with home-made instruments and with a vision of a financial reward for their art. Usually, however, these bands tend to reap a few mocking glances and a few thrown rotten tomatoes. Then the band starts to quarrel and fall apart.'),
    ('encounter.c.21.desc', 'cs', 'Pro hudebně nadané je ve Withergate velice těžké se prosadit. Například teď se na rohu sešla parta kluků s podomácku vyrobenými nástroji a s vidinou finanční odměny za své umění. Obvykle ale tyto kapely spíše sklidí několik posměšných pohledů a pár vržených nahnilých rajčat, načež se pohádají a rozpadnou. '),
    ('encounter.c.21.succ', 'en', 'The character realized that it was rather a poor performance. "In the seventeenth bar, the bass played c sharp - why," asked the character the principal who was collecting a entrance fee to a hat. "Damn it, the bass was supposed to play D flat there!" He calmed the character down by returning the entrance fee. He did not notice that the character did not pay any entrance fees. Everyone is happy today... Oh no, just a few minutes later, a gunfire erupted from a window above out character, but it was only a bass player getting shot for playing the wrong riff three times in a row, and bass players are two a cap in Withergate.'),
    ('encounter.c.21.succ', 'cs', 'Postava se nenechala lacinou produkcí rozhodit. “V sedmnáctém taktu hrála basa cis - proč?”, zeptala se obejdy, který vybíral příspěvek na kapelu do klobouku. “To je ale blb, měl tam zahrát des”, pokusil se rozhořčení zmírnit principál. Postavu uklidnil vrácením vstupného. Nijak nezaznamenal, že žádné vstupné neplatila. Dnešní den má pouze vítěze... Aha, tak ne, právě kdesi za postavou zarachotila střelba. Ale to nic, to jen zastřelili basáka za to, že třikrát po sobě zahrál nesprávnou figuru.'),
    ('encounter.c.21.fail', 'en', 'The character threw a few caps into the hat without realizing how bad the band was.'),
    ('encounter.c.21.fail', 'cs', 'Postava do klobouku vhodila několik zátek, aniž by pochopila, jak moc byla kapela špatná. Alespoň neměla pocit ze ztraceného dne.'),
    ('encounter.c.22.desc', 'en', 'The City Hall tries to keep under control all of the cultural life in the city. A noisy band had just finished its performance, with a lot of hairy audience. Gentlemen in long coats approached two of the hippies and asked about the impressions from the performance. "I haven`t heard that kind of music anywhere. It was like a creak and a squeak, and I would never want to experience that kind of entertainment produced by this group again," said the first of the interrogated spontaneously.'),
    ('encounter.c.22.desc', 'cs', 'Městská radnice se snaží mít pod dohledem veškerý kulturní život ve městě. Právě proběhlo vystoupení nějaké hlučné kapely, z něhož se spokojeně vracela hromada vlasatých lidí. Ke dvěma z nich přistoupili pánové v dlouhých kabátech a ptali se na dojmy z představení. “Takový druh hudby jsem nikde neslyšel. Podobalo se to vrzání a skřípání a druh zábavy, který byl produkován touto skupinou, bych už zažít nechtěl”, uvedl zcela spontánně první z vyslýchaných.'),
    ('encounter.c.22.succ', 'en', 'The members of the secret police were satisfied with the interrogation and left to bother someone else. Our character understood that the hairy pair was a part of the resistance against the City Hall. The character cried probably secret passwords to them: "Punk’s not dead! No future! Anarchy in the Withergate!" The reward was a obscene gesture and the feeling that the character was part of the fight against the system.'),
    ('encounter.c.22.succ', 'cs', '“Tak to je už třicátý případ, kdy nám někdo řekl přesně tohle”, pronesli příslušníci tajné policie a spokojeně odešli. Postava pochopila, že to bylo součástí odboje proti Radnici. “Punk’s not dead, vy volové!” zvolala pravděpodobné tajné heslo na vyslýchané pankáče. Odměnou bylo rozverné oplzlé gesto a pocit, že je postava součástí boje proti systému. '),
    ('encounter.c.22.fail', 'en', 'The character watched the interrogation incomprehensibly. And then left incomprehensibly. And later told the others in the clan that the character did not understand today`s experience.'),
    ('encounter.c.22.fail', 'cs', 'Postava nechápavě přihlížela výslechu. Poté nechápavě odešla. Ostatním v klanu vyprávěla, že dnešní zážitek nechápe. '),
    ('encounter.c.23.desc', 'en', 'A really disgusting individual joined the character in the city. It had an exquisite collection of skin diseases. For some reason, the monster wanted to shake hands with the character.'),
    ('encounter.c.23.desc', 'cs', 'K postavě se v ulicích přihrnulo nechutně vyhlížející individuum se solidně vyhlížející sbírkou kožních onemocnění. Z nějakého důvodu toužilo potřást postavě pravicí.'),
    ('encounter.c.23.succ', 'en', 'The character recognized the danger of a contact with the infected person and quickly hid in the crowd. The hideous man chose another victim, who reacted angrily. The two started to fight. Before the brawl moved to a side street, something fell to the ground. The character verified that it was not a piece of leprous tissue, and took the valuable.'),
    ('encounter.c.23.succ', 'cs', 'Postava rozpoznala nebezpečí kontaktu s nakaženým a včas se skryla v davu. Nakažený si na realizaci své osahávací touhy vybral jinou oběť. Ta reagovala podrážděně, začala se s nakaženým prát. Rvačka se přesunula do vedlejší ulice, před tím oběti něco odpadlo na zem. Postava ověřila, že to nebyl kus malomocné tkáně, a odnesla si cenný nález domů.'),
    ('encounter.c.23.fail', 'en', 'A contact has occurred. In the evening, the character became quite ill - perhaps because of that awful memory, perhaps because of the contagious disease. Hopefully this will not have long-term consequences.'),
    ('encounter.c.23.fail', 'cs', 'Došlo ke kontaktu. Večer se z toho postavě udělalo docela špatně - možná z té vzpomínky, možná z té nakažlivé nemoci. Snad to nebude mít dlouhodobé následky.'),
    ('encounter.c.24.desc', 'en', 'An old gladiator approached the character and offered his services: "Ha, you look like you`d appreciate some paid tips for the arena!'),
    ('encounter.c.24.desc', 'cs', 'Postavu oslovil stárnoucí gladiátor a vnutil jí své služby. “Hele ty, vypadáš, jako že neumíš do pěti počítat, a že bys proto ocenil trochu placených tipů do arény!”'),
    ('encounter.c.24.succ', 'en', 'The character must have fascinated the gladiator because he told her a lot of stories from the arena, and in the end he didn`t want anything in return. “Keep the caps, I`m glad I shared my knowledge with someone who will use it!'),
    ('encounter.c.24.succ', 'cs', 'Postava zřejmě gladiátora zaujala, protože jí dlouze vyprávěl o svých zážitcích a o zápasech staré doby, a nakonec za to nic nechtěl. “Zátky si nech, jsem rád, že jsem svoje znalosti předal někomu, kdo je využije!”'),
    ('encounter.c.24.fail', 'en', 'Either he was a bad teacher or the character is a bad student. More like the latter. The character had to pay several caps for nothing, only incomprehension.'),
    ('encounter.c.24.fail', 'cs', 'Buď to byl špatný učitel, nebo je postava špatný žák. Spíš to druhé. Gladiátor se pokoušel předat své znalosti, ale nesetkal se s pochopením. Nakonec vyinkasoval několik zátek.'),
    ('encounter.c.25.desc', 'en', 'Walking through the city, the character noticed a group of people working on the foundations of a building. The Council has probably decided on building its new representative city hall. Right when the character was about to turn a worker shouted at it… "Hey, you, how `bout some help? I am too old for this and if you lend me a hand, I will split my wage with you!" The gullible character agreed, thinking of easily gained caps. It grabbed a shovel and set to digging a hole under the supervision of the idling worker...'),
    ('encounter.c.25.desc', 'cs', 'Když postava procházela městem s trochou nasbíraného šrotu, narazila na skupinu lidí, kteří kopali základy budovy. Zřejmě se radnice rozhodla pro výstavbu nového sídla… V momentě, kdy se postava chtěla otočit, na ni zavolal jeden z dělníků: "Hej, ty tam… už nemůžu a zbývá mi ještě vykopat tenhle příkop. Když mi píchneš, dám ti za to půlku vydělaných zátek.” Důvěřivá postava si s vidinou snadného zisku vzala do rukou lopatu a pustila se do práce, zatímco dělník se na ni unaveně díval.'),
    ('encounter.c.25.succ', 'en', '...but when it looked backed once again, the guy was nowhere in sight. The character decided to find him but hastily returned to work when a guard with an impressive looking club emerged from around the corner. The character cursed and was forced to work all afternoon. Luckily, when the convicts (which the character now realized they were) were returning to the cells, the character used its knowledge of the city and managed to disappear into one of the shady alleys… with the shovel as a nice bonus.'),
    ('encounter.c.25.succ', 'cs', 'Když se ale postava ohlédla, dělník nikde nebyl. Rozhodla se ho jít hledat, ale poté, co k ní zpoza rohu zamířil strážný s obuškem, rychle si to rozmyslela. Musela tak pilně pracovat celý den. Když se potom skupinka trestanců - za což si postava nadávala, že ji to nenapadlo dříve - měla odebrat zpět do cel, měla postava štěstí a s využitím znalostí města se jí podařilo nenápadně vytratit… i s tou lopatou.'),
    ('encounter.c.25.fail', 'en', '...but when it looked backed once again, the guy was nowhere in sight. The character decided to find him but hastily returned to work when a guard with an impressive looking club emerged from around the corner. The character cursed and was forced to work all afternoon. The character decided to flee as were the convicts (which the character now realized they were) returning to their cells. Despite receiving a few hits, the character overcame the guard and fled to a nearby alley. We can really consider that a success compared to a lengthy stay in a prison cell.'),
    ('encounter.c.25.fail', 'cs', 'Když se ale postava ohlédla, dělník nikde nebyl. Rozhodla se ho jít hledat, ale poté, co k ní zpoza rohu zamířil strážný s obuškem, rychle si to rozmyslela. Musela tak pilně pracovat celý den. Když se potom skupinka trestanců - za což si postava nadávala, že ji to nenapadlo dříve - měla odebrat zpět do cel, rozhodla se postava utéct. Přes pár ran obuškem se postavě podařilo přemoci strážného a zmizet ve tmě. Rozhodně to můžeme nazývat úspěchem - kdo ví, jak dlouho by postava jinak seděla...');
